import json
import datetime
from typing import List, Dict, Tuple

def load_backup_schedules(config_file: str) -> List[Dict]:
    """Load backup schedules from JSON config"""
    with open(config_file, 'r') as f:
        return json.load(f)['schedules']

def find_max_maintenance_window(schedules: List[Dict]) -> int:
    """Find the maximum guaranteed maintenance window between backups.

    Returns the largest gap (in minutes) that is guaranteed to exist
    between any backup jobs, regardless of when each schedule started.
    """
    # Current buggy implementation
    periods = [s['period_minutes'] for s in schedules]

    # Bug: Just returns the minimum period divided by 2
    # This is completely wrong for finding guaranteed gaps
    return min(periods) // 2

def main():
