    max_window = find_max_maintenance_window(schedules)

    # Log the result
    with open('maintenance_windows.txt', 'w') as f:
        f.write(f"Maximum guaranteed maintenance window: {max_window} minutes\n")
        f.write(f"Analyzed {len(schedules)} backup schedules\n")

    print(f"Maximum guaranteed maintenance window: {max_window} minutes")

if __name__ == "__main__":
