@strawberry.type
class User:
    id: strawberry.ID
    email: str
    name: str
    created_at: datetime
    orders: List['Order'] = strawberry.field(default_factory=list)

@strawberry.type
class Review:
    id: strawberry.ID
    rating: int
    comment: str
    product: Product
    user: User
