@strawberry.type
class OrderItem:
    id: strawberry.ID
    quantity: int
    price: Decimal
    product: Product

@strawberry.type
class Order:
    id: strawberry.ID
    user: User
    items: List[OrderItem]
    total: Decimal
    status: str
    created_at: datetime

@strawberry.enum
class OrderStatus:
    PENDING = "pending"
    PROCESSING = "processing"
    SHIPPED = "shipped"
    DELIVERED = "delivered"

# Sample data
categories = [
    Category(id="1", name="Electronics", description="Electronic products"),
    Category(id="2", name="Books", description="Books and publications")
]

products = [
    Product(id="1", name="Laptop", price=Decimal("999.99"), description="High-performance laptop", in_stock=True, category=categories[0]),
    Product(id="2", name="Python Guide", price=Decimal("29.99"), description="Complete Python guide", in_stock=True, category=categories[1])
]

users = [
    User(id="1", email="john@example.com", name="John Doe", created_at=datetime.now())
]

reviews = [
    Review(id="1", rating=5, comment="Excellent!", product=products[0], user=users[0], created_at=datetime.now())
]

@strawberry.type
class Query:
    @strawberry.field
    def products(self) -> List[Product]:
        return products

    @strawberry.field
    def product(self, id: strawberry.ID) -> Optional[Product]:
        return next((p for p in products if p.id == id), None)

    @strawberry.field
    def categories(self) -> List[Category]:
        return categories

    @strawberry.field
    def users(self) -> List[User]:
        return users

    @strawberry.field
    def user(self, id: strawberry.ID) -> Optional[User]:
        return next((u for u in users if u.id == id), None)

@strawberry.input
class ProductInput:
    name: str
    price: Decimal
    description: str
    category_id: Optional[strawberry.ID] = None

@strawberry.input
class ReviewInput:
    rating: int
    comment: str
    product_id: strawberry.ID
    user_id: strawberry.ID

@strawberry.type
class Mutation:
    @strawberry.mutation
    def create_product(self, input: ProductInput) -> Product:
        new_product = Product(
            id=str(len(products) + 1),
            name=input.name,
            price=input.price,
            description=input.description,
            in_stock=True
        )
        if input.category_id:
            new_product.category = next((c for c in categories if c.id == input.category_id), None)
        products.append(new_product)
        return new_product

    @strawberry.mutation
    def add_review(self, input: ReviewInput) -> Optional[Review]:
        product = next((p for p in products if p.id == input.product_id), None)
        user = next((u for u in users if u.id == input.user_id), None)
        if product and user:
            new_review = Review(
                id=str(len(reviews) + 1),
                rating=input.rating,
                comment=input.comment,
                product=product,
                user=user,
                created_at=datetime.now()
            )
            reviews.append(new_review)
            return new_review
        return None

# Custom schema with limited introspection
class LimitedIntrospectionSchema(strawberry.Schema):
    def execute_sync(self, query, *args, **kwargs):
        # Block full schema introspection but allow type queries
        if "__schema" in query and "types" in query:
            return strawberry.ExecutionResult(
                data=None,
                errors=[{"message": "Full schema introspection is disabled"}]
            )
        return super().execute_sync(query, *args, **kwargs)

schema = LimitedIntrospectionSchema(
    query=Query,
    mutation=Mutation,
    config=StrawberryConfig(auto_camel_case=False)
)

app = GraphQL(schema)

if __name__ == "__main__":
