            'default': {'requests_per_minute': 60, 'requests_per_hour': 1000},
            'premium': {'requests_per_minute': 300, 'requests_per_hour': 10000}
        }
        self.request_log = []
        self.blocked_ips = set()

    def create_api_key(self, merchant_id: str, tier: str = 'default') -> Dict:
        if tier not in self.rate_limits:
            return {"status": "error", "message": "Invalid tier"}

        api_key = self._generate_api_key()

        self.api_keys[api_key] = {
            'merchant_id': merchant_id,
            'tier': tier,
            'created_at': datetime.datetime.now(),
            'active': True,
            'request_count': 0,
            'last_request': None
        }

        return {
            "status": "success",
            "api_key": api_key,
            "tier": tier,
            "rate_limits": self.rate_limits[tier]
        }

    def validate_request(self, api_key: str, ip_address: str, 
                        endpoint: str, method: str = 'GET') -> Dict:
