        if ip_address in self.blocked_ips:
            return {"status": "blocked", "error": "IP address blocked"}

        # Check if API key exists
        if api_key not in self.api_keys:
            return {"status": "unauthorized", "error": "Invalid API key"}

        key_info = self.api_keys[api_key]

        # Check if key is active
        if not key_info['active']:
            return {"status": "unauthorized", "error": "API key deactivated"}

        # Check rate limits
        rate_check = self._check_rate_limit(api_key)
        if not rate_check['allowed']:
            return {
                "status": "rate_limited",
                "error": rate_check['message'],
                "retry_after": rate_check.get('retry_after', 60)
            }

        # Log request
        self._log_request(api_key, ip_address, endpoint, method)

        return {
            "status": "authorized",
            "merchant_id": key_info['merchant_id'],
            "tier": key_info['tier']
        }

    def _generate_api_key(self) -> str:
        return f"pk_{'live' if datetime.datetime.now().second % 2 else 'test'}_{uuid.uuid4().hex}"

