        # Get requests in last minute
        minute_ago = now - datetime.timedelta(minutes=1)
        minute_requests = sum(
            1 for req in self.request_log 
            if req['api_key'] == api_key and req['timestamp'] > minute_ago
        )

        if minute_requests >= tier_limits['requests_per_minute']:
            return {
                'allowed': False,
                'message': 'Minute rate limit exceeded',
                'retry_after': 60
            }

        # Get requests in last hour
        hour_ago = now - datetime.timedelta(hours=1)
        hour_requests = sum(
            1 for req in self.request_log 
            if req['api_key'] == api_key and req['timestamp'] > hour_ago
        )

        if hour_requests >= tier_limits['requests_per_hour']:
            return {
                'allowed': False,
                'message': 'Hourly rate limit exceeded',
                'retry_after': 3600
            }

