    def _log_request(self, api_key: str, ip_address: str, endpoint: str, method: str):
        request = {
            'api_key': api_key,
            'ip_address': ip_address,
            'endpoint': endpoint,
            'method': method,
            'timestamp': datetime.datetime.now()
