                transaction['error'] = 'Invalid card number'

        batch['processed_count'] = processed
        batch['failed_count'] = failed
        batch['status'] = 'processed'
        batch['processing_completed_at'] = datetime.datetime.now()

        processing_time = (batch['processing_completed_at'] - batch['processing_started_at']).total_seconds()

        return {
            "status": "success",
            "batch_id": batch_id,
            "processed": processed,
            "failed": failed,
            "processing_time_seconds": processing_time
