from typing import Dict, List, Optional
import datetime
import uuid


class SettlementProcessor:
    def __init__(self):
        self.settlements = {}
        self.pending_transactions = []
        self.settlement_schedule = {
            'standard': 2,  # T+2 days
            'express': 1,   # T+1 days
            'same_day': 0   # T+0 days
        }

    def add_transaction(self, transaction: Dict) -> Dict:
        if transaction.get('status') != 'success':
            return {"status": "error", "message": "Only successful transactions can be settled"}

        pending = {
            'transaction_id': transaction.get('transaction_id'),
            'merchant_id': transaction.get('merchant_id'),
            'amount': Decimal(str(transaction.get('amount', 0))),
            'fee': Decimal(str(transaction.get('fee', 0))),
            'created_at': datetime.datetime.now(),
            'settlement_type': transaction.get('settlement_type', 'standard')
        }

        self.pending_transactions.append(pending)

        return {
            "status": "success",
            "message": "Transaction added to settlement queue",
            "expected_settlement": self._calculate_settlement_date(pending['settlement_type'])
        }

    def create_settlement_batch(self, merchant_id: str) -> Dict:
        eligible_transactions = self._get_eligible_transactions(merchant_id)

        if not eligible_transactions:
            return {"status": "error", "message": "No eligible transactions for settlement"}

        settlement_id = self._generate_settlement_id()

        total_amount = sum(t['amount'] for t in eligible_transactions)
        total_fees = sum(t['fee'] for t in eligible_transactions)
        net_amount = total_amount - total_fees

        settlement = {
            'id': settlement_id,
            'merchant_id': merchant_id,
            'status': 'pending',
            'created_at': datetime.datetime.now(),
            'transaction_count': len(eligible_transactions),
            'gross_amount': total_amount,
            'total_fees': total_fees,
            'net_amount': net_amount,
            'transactions': eligible_transactions
        }

        self.settlements[settlement_id] = settlement

        # Remove from pending
        for trans in eligible_transactions:
            self.pending_transactions.remove(trans)

        return {
            "status": "success",
            "settlement_id": settlement_id,
            "transaction_count": len(eligible_transactions),
            "net_amount": str(net_amount)
        }

    def _get_eligible_transactions(self, merchant_id: str) -> List[Dict]:
        eligible = []
        now = datetime.datetime.now()

        for trans in self.pending_transactions:
            if trans['merchant_id'] != merchant_id:
                continue

            days_waiting = (now - trans['created_at']).days
            required_days = self.settlement_schedule.get(trans['settlement_type'], 2)

            if days_waiting >= required_days:
                eligible.append(trans)

        return eligible

    def _calculate_settlement_date(self, settlement_type: str) -> str:
        days = self.settlement_schedule.get(settlement_type, 2)
        settlement_date = datetime.datetime.now() + datetime.timedelta(days=days)

        # Skip weekends
        while settlement_date.weekday() in [5, 6]:  # Saturday, Sunday
            settlement_date += datetime.timedelta(days=1)

        return settlement_date.strftime('%Y-%m-%d')

    def _generate_settlement_id(self) -> str:
        return f"SET{uuid.uuid4().hex[:12].upper()}"

    def process_settlement(self, settlement_id: str) -> Dict:
        if settlement_id not in self.settlements:
            return {"status": "error", "message": "Settlement not found"}

        settlement = self.settlements[settlement_id]

        if settlement['status'] != 'pending':
            return {"status": "error", "message": f"Settlement already {settlement['status']}"}

        # Simulate processing
        settlement['status'] = 'processing'
        settlement['processing_started_at'] = datetime.datetime.now()

        # Simulate bank transfer
        if self._initiate_bank_transfer(settlement):
            settlement['status'] = 'completed'
            settlement['completed_at'] = datetime.datetime.now()
            settlement['bank_reference'] = f"REF{uuid.uuid4().hex[:8].upper()}"

            return {
                "status": "success",
                "settlement_id": settlement_id,
                "bank_reference": settlement['bank_reference'],
                "amount_transferred": str(settlement['net_amount'])
            }
        else:
            settlement['status'] = 'failed'
            settlement['failed_at'] = datetime.datetime.now()
            settlement['failure_reason'] = 'Bank transfer failed'

            return {
                "status": "failed",
                "settlement_id": settlement_id,
                "error": "Bank transfer failed"
            }

    def _initiate_bank_transfer(self, settlement: Dict) -> bool:
        # Simulate bank transfer success rate
        return settlement['net_amount'] > 0

    def get_settlement_status(self, settlement_id: str) -> Optional[Dict]:
        if settlement_id not in self.settlements:
            return None

        settlement = self.settlements[settlement_id]

        return {
            'settlement_id': settlement_id,
            'merchant_id': settlement['merchant_id'],
            'status': settlement['status'],
            'transaction_count': settlement['transaction_count'],
            'net_amount': str(settlement['net_amount']),
            'created_at': settlement['created_at'].isoformat(),
            'bank_reference': settlement.get('bank_reference')
        }

    def get_pending_amount(self, merchant_id: str) -> Dict:
        total_pending = Decimal('0')
        transaction_count = 0

        for trans in self.pending_transactions:
            if trans['merchant_id'] == merchant_id:
                net_amount = trans['amount'] - trans['fee']
                total_pending += net_amount
                transaction_count += 1

        return {
            'merchant_id': merchant_id,
            'pending_amount': str(total_pending),
            'pending_transactions': transaction_count,
            'next_settlement_date': self._get_next_settlement_date(merchant_id)
        }

    def _get_next_settlement_date(self, merchant_id: str) -> Optional[str]:
        earliest_eligible = None

        for trans in self.pending_transactions:
            if trans['merchant_id'] == merchant_id:
                settlement_date = trans['created_at'] + datetime.timedelta(
                    days=self.settlement_schedule.get(trans['settlement_type'], 2)
                )

                # Skip weekends
                while settlement_date.weekday() in [5, 6]:
                    settlement_date += datetime.timedelta(days=1)

                if earliest_eligible is None or settlement_date < earliest_eligible:
                    earliest_eligible = settlement_date

        return earliest_eligible.strftime('%Y-%m-%d') if earliest_eligible else None

    def get_settlement_history(self, merchant_id: str, days: int = 30) -> List[Dict]:
        cutoff_date = datetime.datetime.now() - datetime.timedelta(days=days)
        history = []

        for settlement in self.settlements.values():
            if (settlement['merchant_id'] == merchant_id and 
                settlement['created_at'] > cutoff_date):
                history.append({
                    'settlement_id': settlement['id'],
                    'date': settlement['created_at'].strftime('%Y-%m-%d'),
                    'status': settlement['status'],
                    'transaction_count': settlement['transaction_count'],
                    'net_amount': str(settlement['net_amount']),
                    'bank_reference': settlement.get('bank_reference')
                })

        return sorted(history, key=lambda x: x['date'], reverse=True)

    def generate_settlement_report(self, start_date: datetime.date, 
                                 end_date: datetime.date) -> Dict:
        total_settlements = 0
        total_amount = Decimal('0')
        by_status = {}
        by_merchant = {}

        for settlement in self.settlements.values():
            settlement_date = settlement['created_at'].date()

            if start_date <= settlement_date <= end_date:
                total_settlements += 1
                total_amount += settlement['net_amount']

                # Count by status
                status = settlement['status']
                by_status[status] = by_status.get(status, 0) + 1

                # Sum by merchant
                merchant_id = settlement['merchant_id']
                if merchant_id not in by_merchant:
                    by_merchant[merchant_id] = {
                        'count': 0,
                        'total_amount': Decimal('0')
                    }
                by_merchant[merchant_id]['count'] += 1
                by_merchant[merchant_id]['total_amount'] += settlement['net_amount']

        return {
            'period': f"{start_date} to {end_date}",
            'total_settlements': total_settlements,
            'total_amount': str(total_amount),
            'average_settlement': str(total_amount / total_settlements) if total_settlements > 0 else '0',
            'by_status': by_status,
            'top_merchants': sorted(
                [{'merchant_id': k, 'count': v['count'], 'amount': str(v['total_amount'])} 
                 for k, v in by_merchant.items()],
                key=lambda x: x['amount'],
                reverse=True
            )[:10]
