class FraudDetector:
    def __init__(self):
        self.transaction_history = {}
        self.blacklisted_cards = set()
        self.suspicious_ips = set()
        self.risk_thresholds = {
            'velocity': 5,  # transactions per hour
            'amount_spike': Decimal('3.0'),  # multiplier of average
            'location_changes': 3  # different locations per day
        }

    def analyze_transaction(self, card_number: str, amount: Decimal, 
                          ip_address: str, location: Optional[str] = None) -> Dict:
        risk_score = 0
        risk_factors = []

        # Check blacklist
        if self._is_blacklisted(card_number):
            return {"risk_level": "high", "score": 100, "factors": ["blacklisted_card"], "action": "block"}

        # Check suspicious IP
        if ip_address in self.suspicious_ips:
            risk_score += 30
            risk_factors.append("suspicious_ip")

        # Velocity check
        velocity_risk = self._check_velocity(card_number)
        if velocity_risk > 0:
            risk_score += velocity_risk
            risk_factors.append(f"high_velocity_{velocity_risk}")

        # Amount anomaly check
        amount_risk = self._check_amount_anomaly(card_number, amount)
        if amount_risk > 0:
            risk_score += amount_risk
            risk_factors.append(f"amount_anomaly_{amount_risk}")

        # Location check
        if location:
            location_risk = self._check_location_pattern(card_number, location)
            if location_risk > 0:
                risk_score += location_risk
                risk_factors.append(f"location_anomaly_{location_risk}")

        # Pattern analysis
        pattern_risk = self._analyze_patterns(card_number, amount, ip_address)
        if pattern_risk > 0:
            risk_score += pattern_risk
            risk_factors.append("suspicious_pattern")

        # Determine risk level and action
        if risk_score >= 70:
            risk_level = "high"
            action = "block"
        elif risk_score >= 40:
            risk_level = "medium" 
            action = "review"
        else:
            risk_level = "low"
            action = "approve"

        # Log transaction
        self._log_transaction(card_number, amount, ip_address, location, risk_score)

        return {
            "risk_level": risk_level,
            "score": risk_score,
            "factors": risk_factors,
            "action": action
        }

    def _is_blacklisted(self, card_number: str) -> bool:
        return card_number in self.blacklisted_cards

    def _check_velocity(self, card_number: str) -> int:
        if card_number not in self.transaction_history:
            return 0

        recent_transactions = self._get_recent_transactions(card_number, hours=1)
        if len(recent_transactions) >= self.risk_thresholds['velocity']:
            return 25
        elif len(recent_transactions) >= self.risk_thresholds['velocity'] - 2:
            return 15
        return 0

    def _check_amount_anomaly(self, card_number: str, amount: Decimal) -> int:
        if card_number not in self.transaction_history:
            return 0

        amounts = [t['amount'] for t in self.transaction_history[card_number][-20:]]
        if len(amounts) < 5:
            return 0

        avg_amount = statistics.mean(amounts)
        if amount > avg_amount * self.risk_thresholds['amount_spike']:
            return 20
        elif amount > avg_amount * 2:
            return 10
        return 0

    def _check_location_pattern(self, card_number: str, location: str) -> int:
        if card_number not in self.transaction_history:
            return 0

        today_transactions = self._get_recent_transactions(card_number, hours=24)
        locations = set(t.get('location') for t in today_transactions if t.get('location'))

        if len(locations) >= self.risk_thresholds['location_changes']:
            return 25
        elif len(locations) >= 2 and location not in locations:
            return 15
        return 0

    def _analyze_patterns(self, card_number: str, amount: Decimal, ip_address: str) -> int:
        # Check for card testing pattern (small amounts)
        if amount < Decimal('1.00'):
            recent = self._get_recent_transactions(card_number, hours=1)
            small_amounts = sum(1 for t in recent if t['amount'] < Decimal('1.00'))
            if small_amounts >= 3:
                return 30

        # Check for round number pattern
        if str(amount).endswith('00.00') and amount > Decimal('100'):
            return 10

        return 0

    def _get_recent_transactions(self, card_number: str, hours: int) -> List[Dict]:
        if card_number not in self.transaction_history:
            return []

        cutoff_time = datetime.datetime.now() - datetime.timedelta(hours=hours)
        return [t for t in self.transaction_history[card_number] 
                if t['timestamp'] > cutoff_time]

    def _log_transaction(self, card_number: str, amount: Decimal, 
                        ip_address: str, location: Optional[str], risk_score: int):
        if card_number not in self.transaction_history:
            self.transaction_history[card_number] = []

        self.transaction_history[card_number].append({
            'timestamp': datetime.datetime.now(),
            'amount': amount,
            'ip_address': ip_address,
            'location': location,
            'risk_score': risk_score
        })

        # Keep only last 100 transactions per card
        if len(self.transaction_history[card_number]) > 100:
            self.transaction_history[card_number] = self.transaction_history[card_number][-100:]

    def add_to_blacklist(self, card_number: str) -> bool:
        if card_number and len(card_number) >= 13:
            self.blacklisted_cards.add(card_number)
            return True
        return False

    def add_suspicious_ip(self, ip_address: str) -> bool:
        if ip_address:
            self.suspicious_ips.add(ip_address)
            return True
        return False

    def get_card_risk_profile(self, card_number: str) -> Dict:
        if card_number not in self.transaction_history:
            return {"status": "no_history", "transactions": 0}

        transactions = self.transaction_history[card_number]
        risk_scores = [t['risk_score'] for t in transactions]

