import datetime
import re


class NotificationService:
    def __init__(self):
        self.templates = {
            'payment_success': "Your payment of ${amount} has been processed successfully. Transaction ID: {transaction_id}",
            'payment_failed': "Your payment of ${amount} could not be processed. Reason: {reason}",
            'refund_approved': "Your refund of ${amount} has been approved. Refund ID: {refund_id}",
            'suspicious_activity': "Suspicious activity detected on your account. Please contact support.",
            'batch_completed': "Batch {batch_id} processing completed. Processed: {processed}, Failed: {failed}"
        }
        self.notifications = []
        self.preferences = {}

    def send_notification(self, recipient: str, template_name: str, 
                         params: Dict, channel: str = 'email') -> Dict:
        if template_name not in self.templates:
            return {"status": "error", "message": "Template not found"}

        if not self._validate_recipient(recipient, channel):
            return {"status": "error", "message": f"Invalid {channel} recipient"}

        # Check preferences
        if not self._check_preferences(recipient, template_name):
            return {"status": "skipped", "message": "User opted out of this notification type"}

        # Format message
        try:
            message = self.templates[template_name].format(**params)
        except KeyError as e:
            return {"status": "error", "message": f"Missing parameter: {e}"}

        notification = {
            'id': self._generate_notification_id(),
            'recipient': recipient,
            'channel': channel,
            'template': template_name,
            'message': message,
            'status': 'sent',
            'sent_at': datetime.datetime.now()
        }

        self.notifications.append(notification)

        return {
            "status": "success",
            "notification_id": notification['id'],
            "message": message
        }

    def _validate_recipient(self, recipient: str, channel: str) -> bool:
        if channel == 'email':
            return bool(re.match(r'^[\w\.-]+@[\w\.-]+\.\w+$', recipient))
        elif channel == 'sms':
            return bool(re.match(r'^\+?1?\d{10,14}$', recipient))
        return False

    def _check_preferences(self, recipient: str, template_name: str) -> bool:
        if recipient not in self.preferences:
            return True  # Default to sending if no preferences set

        prefs = self.preferences[recipient]

        # Check if opted out of all notifications
        if prefs.get('all_notifications') is False:
            return False

        # Check specific notification type
        if prefs.get(template_name) is False:
            return False

        return True

    def _generate_notification_id(self) -> str:
        return f"NOTIF{datetime.datetime.now().strftime('%Y%m%d%H%M%S%f')}"

    def set_preferences(self, recipient: str, preferences: Dict) -> Dict:
        self.preferences[recipient] = preferences

        return {
            "status": "success",
            "recipient": recipient,
            "preferences": preferences
        }

    def get_notification_history(self, recipient: str, days: int = 7) -> List[Dict]:
        cutoff_time = datetime.datetime.now() - datetime.timedelta(days=days)

        history = []
        for notification in self.notifications:
            if (notification['recipient'] == recipient and 
                notification['sent_at'] > cutoff_time):
                history.append({
                    'id': notification['id'],
                    'template': notification['template'],
                    'channel': notification['channel'],
                    'sent_at': notification['sent_at'].isoformat(),
