import json


class CurrencyConverter:
    def __init__(self):
        self.rates = {
            'USD': Decimal('1.0'),
            'EUR': Decimal('0.85'),
            'GBP': Decimal('0.73'),
            'JPY': Decimal('110.0'),
            'CAD': Decimal('1.25'),
            'AUD': Decimal('1.35')
        }
        self.cache = {}

    def convert(self, amount: Decimal, from_currency: str, to_currency: str) -> Optional[Decimal]:
        if from_currency not in self.rates or to_currency not in self.rates:
            return None

        if from_currency == to_currency:
            return amount

        cache_key = f"{from_currency}:{to_currency}"
        if cache_key in self.cache:
            rate = self.cache[cache_key]
        else:
            rate = self.rates[to_currency] / self.rates[from_currency]
            self.cache[cache_key] = rate

        return amount * rate

    def update_rate(self, currency: str, rate: Decimal) -> bool:
        if currency == 'USD':
            return False  # USD is base currency

        if rate <= 0:
            return False

        self.rates[currency] = rate
        self.cache.clear()  # Invalidate cache
        return True

    def get_supported_currencies(self) -> list:
        return list(self.rates.keys())

    def bulk_convert(self, amounts: Dict[str, Decimal], target_currency: str) -> Dict[str, Optional[Decimal]]:
        results = {}
        for currency, amount in amounts.items():
            try:
                results[currency] = self.convert(amount, currency, target_currency)
            except Exception:
                results[currency] = None
        return results

    def get_exchange_rate(self, from_currency: str, to_currency: str) -> Optional[Decimal]:
        if from_currency not in self.rates or to_currency not in self.rates:
