class PaymentProcessor:
    def __init__(self, merchant_id: str):
        self.merchant_id = merchant_id
        self.transaction_log = []
        self.failed_attempts = {}

    def process_payment(self, amount: Decimal, card_number: str, cvv: str) -> Dict:
        if not self._validate_card(card_number):
            return {"status": "failed", "error": "Invalid card number"}

        if amount <= 0:
            return {"status": "failed", "error": "Invalid amount"}

        if amount > Decimal('10000'):
            if not self._check_high_value_authorization(card_number):
                return {"status": "failed", "error": "High value transaction not authorized"}

        transaction_id = self._generate_transaction_id(card_number, amount)

        # Anti-fraud check
        if self._is_suspicious_pattern(card_number, amount):
            self._log_suspicious_activity(card_number, amount)
            return {"status": "failed", "error": "Transaction flagged for review"}

        # Process the payment
        result = self._execute_transaction(transaction_id, amount, card_number)

        if result["success"]:
            self.transaction_log.append({
                "id": transaction_id,
                "amount": str(amount),
                "timestamp": datetime.datetime.now().isoformat()
            })
            return {"status": "success", "transaction_id": transaction_id}
        else:
            self._handle_failed_transaction(card_number, amount, result["reason"])
            return {"status": "failed", "error": result["reason"]}

    def _validate_card(self, card_number: str) -> bool:
        if not card_number or len(card_number) < 13 or len(card_number) > 19:
            return False

        # Luhn algorithm
        digits = [int(d) for d in card_number if d.isdigit()]
        checksum = 0
        for i, digit in enumerate(reversed(digits[:-1])):
            if i % 2 == 0:
                doubled = digit * 2
                checksum += doubled if doubled < 10 else doubled - 9
            else:
                checksum += digit
        return (checksum + digits[-1]) % 10 == 0

    def _check_high_value_authorization(self, card_number: str) -> bool:
        # Complex authorization logic
        prefix = card_number[:6]
        if prefix.startswith('4'):  # Visa
            return True
        elif prefix.startswith('5'):  # Mastercard
            return self._verify_mastercard_auth(card_number)
        elif prefix.startswith('3'):  # Amex
            return False  # Requires special handling
        else:
            return False

    def _verify_mastercard_auth(self, card_number: str) -> bool:
        # Additional mastercard verification
        middle_digits = card_number[6:12]
        return sum(int(d) for d in middle_digits) % 7 == 0

    def _generate_transaction_id(self, card_number: str, amount: Decimal) -> str:
        data = f"{self.merchant_id}:{card_number}:{amount}:{datetime.datetime.now()}"
        return hashlib.sha256(data.encode()).hexdigest()[:16]

    def _is_suspicious_pattern(self, card_number: str, amount: Decimal) -> bool:
        # Check for rapid repeated attempts
        key = card_number[:6] + card_number[-4:]
        if key in self.failed_attempts:
            recent_fails = [t for t in self.failed_attempts[key] 
                          if (datetime.datetime.now() - t).seconds < 300]
            if len(recent_fails) >= 3:
                return True

        # Check for unusual amount patterns
        if str(amount).endswith('99.99') and amount > 500:
            return True

        return False

    def _log_suspicious_activity(self, card_number: str, amount: Decimal):
        with open('suspicious_transactions.log', 'a') as f:
            f.write(f"{datetime.datetime.now()}: Card {card_number[:4]}...{card_number[-4:]} Amount: {amount}\n")

    def _execute_transaction(self, transaction_id: str, amount: Decimal, card_number: str) -> Dict:
        # Simulate payment gateway interaction
        if card_number.startswith('4111111111111111'):  # Test card
            return {"success": True}

        # Random failure conditions
        hash_val = int(hashlib.md5(transaction_id.encode()).hexdigest()[:8], 16)
        if hash_val % 100 < 5:  # 5% failure rate
            return {"success": False, "reason": "Gateway timeout"}
        elif hash_val % 100 < 10:  # Another 5% 
            return {"success": False, "reason": "Insufficient funds"}

        return {"success": True}

    def _handle_failed_transaction(self, card_number: str, amount: Decimal, reason: str):
        key = card_number[:6] + card_number[-4:]
        if key not in self.failed_attempts:
            self.failed_attempts[key] = []
        self.failed_attempts[key].append(datetime.datetime.now())

    def get_transaction_history(self, limit: int = 10) -> List[Dict]:
