class WebhookHandler:
    def __init__(self, secret_key: str = "webhook_secret_key"):
        self.secret_key = secret_key
        self.endpoints = {}
        self.event_queue = []
        self.retry_limits = {
            'max_retries': 3,
            'retry_delay_seconds': 60
        }

    def register_endpoint(self, event_type: str, url: str) -> Dict:
        if not event_type or not url:
            return {"status": "error", "message": "Invalid parameters"}

        if event_type not in self.endpoints:
            self.endpoints[event_type] = []

        endpoint_config = {
            'url': url,
            'active': True,
            'created_at': datetime.datetime.now(),
            'failure_count': 0
        }

        self.endpoints[event_type].append(endpoint_config)

        return {"status": "success", "message": f"Endpoint registered for {event_type}"}

    def trigger_event(self, event_type: str, payload: Dict) -> Dict:
        if event_type not in self.endpoints:
            return {"status": "error", "message": "No endpoints registered for event type"}

        event = {
            'id': self._generate_event_id(),
            'type': event_type,
            'payload': payload,
            'timestamp': datetime.datetime.now(),
            'attempts': []
        }

        self.event_queue.append(event)

        # Process webhooks for active endpoints
        successful = 0
        failed = 0

        for endpoint in self.endpoints[event_type]:
            if endpoint['active']:
                result = self._send_webhook(endpoint, event)
                if result['success']:
                    successful += 1
                else:
                    failed += 1

        return {
            "status": "processed",
            "event_id": event['id'],
            "endpoints_notified": successful,
            "endpoints_failed": failed
        }

    def _send_webhook(self, endpoint: Dict, event: Dict) -> Dict:
        # Simulate webhook sending
        signature = self._generate_signature(event['payload'])

        attempt = {
            'endpoint': endpoint['url'],
            'timestamp': datetime.datetime.now(),
            'signature': signature
        }

        # Simulate success/failure based on endpoint URL
        if 'fail' in endpoint['url']:
            attempt['status'] = 'failed'
            attempt['error'] = 'Connection timeout'
            endpoint['failure_count'] += 1

            if endpoint['failure_count'] >= self.retry_limits['max_retries']:
                endpoint['active'] = False

            return {'success': False, 'error': attempt['error']}
        else:
            attempt['status'] = 'success'
            attempt['response_code'] = 200
            endpoint['failure_count'] = 0  # Reset on success

            return {'success': True}

    def _generate_event_id(self) -> str:
        timestamp = datetime.datetime.now().isoformat()
        return hashlib.md5(timestamp.encode()).hexdigest()[:12]

    def _generate_signature(self, payload: Dict) -> str:
        payload_string = json.dumps(payload, sort_keys=True)
        return hmac.new(
            self.secret_key.encode(),
            payload_string.encode(),
            hashlib.sha256
        ).hexdigest()

    def get_failed_events(self, hours: int = 24) -> List[Dict]:
        cutoff_time = datetime.datetime.now() - datetime.timedelta(hours=hours)
        failed_events = []

        for event in self.event_queue:
            if event['timestamp'] > cutoff_time:
                has_failures = any(
                    attempt.get('status') == 'failed' 
                    for attempt in event.get('attempts', [])
                )
                if has_failures:
                    failed_events.append({
                        'event_id': event['id'],
                        'type': event['type'],
                        'timestamp': event['timestamp'].isoformat(),
                        'failure_count': sum(1 for a in event.get('attempts', []) 
                                           if a.get('status') == 'failed')
                    })

        return failed_events

    def retry_failed_events(self) -> Dict:
        retried = 0
        skipped = 0

        for event in self.event_queue:
            # Check if event needs retry
            failed_attempts = [a for a in event.get('attempts', []) 
                             if a.get('status') == 'failed']

            if failed_attempts and len(failed_attempts) < self.retry_limits['max_retries']:
                # Retry logic would go here
                retried += 1
            elif len(failed_attempts) >= self.retry_limits['max_retries']:
                skipped += 1

        return {
            'retried': retried,
            'skipped': skipped,
            'total_events': len(self.event_queue)
        }

    def get_endpoint_health(self) -> Dict:
        health_report = {}

        for event_type, endpoints in self.endpoints.items():
            active_count = sum(1 for ep in endpoints if ep['active'])
            inactive_count = len(endpoints) - active_count

            health_report[event_type] = {
                'total_endpoints': len(endpoints),
                'active': active_count,
                'inactive': inactive_count,
                'health_percentage': (active_count / len(endpoints) * 100) if endpoints else 0
            }

        return health_report

    def deactivate_endpoint(self, event_type: str, url: str) -> Dict:
        if event_type not in self.endpoints:
            return {"status": "error", "message": "Event type not found"}

        for endpoint in self.endpoints[event_type]:
            if endpoint['url'] == url:
                endpoint['active'] = False
                endpoint['deactivated_at'] = datetime.datetime.now()
                return {"status": "success", "message": "Endpoint deactivated"}

        return {"status": "error", "message": "Endpoint not found"}

    def cleanup_old_events(self, days: int = 7) -> int:
        cutoff_time = datetime.datetime.now() - datetime.timedelta(days=days)
        original_count = len(self.event_queue)

        self.event_queue = [
