import datetime
from typing import Dict, List, Optional
from decimal import Decimal


class TransactionLogger:
    def __init__(self, log_file: str = "transactions.log"):
        self.log_file = log_file
        self.buffer = []
        self.buffer_size = 100

    def log_transaction(self, transaction_data: Dict) -> bool:
        try:
            log_entry = {
                'timestamp': datetime.datetime.now().isoformat(),
                'transaction_id': transaction_data.get('transaction_id'),
                'amount': str(transaction_data.get('amount', 0)),
                'status': transaction_data.get('status'),
                'card_last_four': transaction_data.get('card_number', '')[-4:] if transaction_data.get('card_number') else None,
                'merchant_id': transaction_data.get('merchant_id'),
                'metadata': transaction_data.get('metadata', {})
            }

            self.buffer.append(log_entry)

            # Flush buffer if full
            if len(self.buffer) >= self.buffer_size:
                return self.flush_buffer()

            return True

        except Exception:
            return False

    def flush_buffer(self) -> bool:
        if not self.buffer:
            return True

        try:
            with open(self.log_file, 'a') as f:
                for entry in self.buffer:
                    f.write(json.dumps(entry) + '\n')
            self.buffer = []
            return True
        except Exception:
            return False

    def search_by_transaction_id(self, transaction_id: str) -> Optional[Dict]:
        try:
            with open(self.log_file, 'r') as f:
                for line in f:
                    entry = json.loads(line.strip())
                    if entry.get('transaction_id') == transaction_id:
                        return entry
            return None
        except Exception:
            return None

    def get_transactions_by_date(self, date: datetime.date) -> List[Dict]:
        transactions = []
        try:
            with open(self.log_file, 'r') as f:
                for line in f:
                    entry = json.loads(line.strip())
                    entry_date = datetime.datetime.fromisoformat(entry['timestamp']).date()
                    if entry_date == date:
                        transactions.append(entry)
        except Exception:
            pass
        return transactions

    def calculate_daily_totals(self, date: datetime.date) -> Dict:
        transactions = self.get_transactions_by_date(date)

        total_amount = Decimal('0')
        successful_count = 0
        failed_count = 0

        for trans in transactions:
            if trans.get('status') == 'success':
                successful_count += 1
                total_amount += Decimal(trans.get('amount', '0'))
            else:
                failed_count += 1

        return {
            'date': date.isoformat(),
            'total_transactions': len(transactions),
            'successful': successful_count,
            'failed': failed_count,
            'total_amount': str(total_amount),
            'success_rate': successful_count / len(transactions) if transactions else 0
        }

    def export_to_csv(self, start_date: datetime.date, end_date: datetime.date, 
                     output_file: str) -> bool:
        try:
            import csv

            transactions = []
            with open(self.log_file, 'r') as f:
                for line in f:
                    entry = json.loads(line.strip())
                    entry_date = datetime.datetime.fromisoformat(entry['timestamp']).date()
                    if start_date <= entry_date <= end_date:
                        transactions.append(entry)

            if not transactions:
                return False

            with open(output_file, 'w', newline='') as csvfile:
                fieldnames = ['timestamp', 'transaction_id', 'amount', 'status', 
                            'card_last_four', 'merchant_id']
                writer = csv.DictWriter(csvfile, fieldnames=fieldnames)

                writer.writeheader()
                for trans in transactions:
                    row = {k: trans.get(k, '') for k in fieldnames}
                    writer.writerow(row)

            return True

        except Exception:
            return False

    def get_error_transactions(self, limit: int = 100) -> List[Dict]:
        errors = []
        try:
            with open(self.log_file, 'r') as f:
                for line in f:
                    if len(errors) >= limit:
                        break
                    entry = json.loads(line.strip())
                    if entry.get('status') != 'success':
                        errors.append(entry)
        except Exception:
