import random
import string
from decimal import Decimal
from typing import Any, Dict, List, Optional


def generate_transaction_id() -> str:
    """Generate a unique transaction ID."""
    timestamp = datetime.datetime.now().strftime('%Y%m%d%H%M%S%f')
    random_suffix = ''.join(random.choices(string.ascii_uppercase + string.digits, k=4))
    return f"TXN{timestamp}{random_suffix}"


def validate_email(email: str) -> bool:
    """Basic email validation."""
    import re
    pattern = r'^[\w\.-]+@[\w\.-]+\.\w+$'
    return bool(re.match(pattern, email))


def format_currency(amount: Decimal, currency: str = 'USD') -> str:
    """Format decimal amount as currency string."""
    if currency == 'USD':
        return f"${amount:,.2f}"
    elif currency == 'EUR':
        return f"€{amount:,.2f}"
    elif currency == 'GBP':
        return f"£{amount:,.2f}"
    else:
        return f"{currency} {amount:,.2f}"


def calculate_percentage(part: Decimal, whole: Decimal) -> Decimal:
    """Calculate percentage with proper handling of zero division."""
    if whole == 0:
        return Decimal('0')
    return (part / whole) * 100


def mask_card_number(card_number: str) -> str:
    """Mask card number showing only last 4 digits."""
    if len(card_number) < 8:
        return "****"
    return f"****{card_number[-4:]}"


def is_business_day(date: datetime.date) -> bool:
    """Check if a date is a business day (Mon-Fri)."""
    return date.weekday() < 5


def get_next_business_day(date: datetime.date) -> datetime.date:
    """Get the next business day after the given date."""
    next_day = date + datetime.timedelta(days=1)
    while not is_business_day(next_day):
        next_day += datetime.timedelta(days=1)
    return next_day


def calculate_hash(data: str) -> str:
    """Calculate SHA256 hash of string data."""
    return hashlib.sha256(data.encode()).hexdigest()


def paginate_list(items: List[Any], page: int = 1, 
                 page_size: int = 10) -> Dict[str, Any]:
    """Paginate a list of items."""
    total_items = len(items)
    total_pages = (total_items + page_size - 1) // page_size

    start_idx = (page - 1) * page_size
    end_idx = start_idx + page_size

    return {
        'items': items[start_idx:end_idx],
        'page': page,
        'page_size': page_size,
        'total_items': total_items,
        'total_pages': total_pages,
        'has_next': page < total_pages,
        'has_previous': page > 1
    }


def sanitize_input(text: str) -> str:
    """Basic input sanitization."""
    # Remove control characters
    import unicodedata
    text = ''.join(ch for ch in text if unicodedata.category(ch)[0] != 'C')
    # Trim whitespace
    return text.strip()


def generate_api_response(status: str, data: Optional[Dict] = None, 
                         error: Optional[str] = None) -> Dict:
    """Generate standardized API response."""
    response = {
        'status': status,
        'timestamp': datetime.datetime.now().isoformat()
    }

    if data is not None:
        response['data'] = data

    if error is not None:
        response['error'] = error

    return response


def calculate_date_range_stats(dates: List[datetime.date]) -> Dict:
    """Calculate statistics for a list of dates."""
    if not dates:
        return {
            'count': 0,
            'earliest': None,
            'latest': None,
            'range_days': 0
        }

    sorted_dates = sorted(dates)

    return {
        'count': len(dates),
        'earliest': sorted_dates[0].isoformat(),
        'latest': sorted_dates[-1].isoformat(),
        'range_days': (sorted_dates[-1] - sorted_dates[0]).days
    }


def validate_amount(amount: Any, min_amount: Decimal = Decimal('0.01'),
                   max_amount: Decimal = Decimal('999999.99')) -> bool:
    """Validate monetary amount."""
    try:
        decimal_amount = Decimal(str(amount))
        return min_amount <= decimal_amount <= max_amount
    except:
        return False


def chunk_list(items: List[Any], chunk_size: int) -> List[List[Any]]:
    """Split a list into chunks of specified size."""
