        # Add high risk fee if applicable  
        if is_high_risk:
            percentage_rate += self.high_risk_fee

        # Calculate total fee
        percentage_fee = amount * percentage_rate
        total_fee = percentage_fee + self.fixed_fee

        # Apply minimum fee
        if total_fee < Decimal('0.50'):
            total_fee = Decimal('0.50')

        return {
            "fee": total_fee,
            "percentage_fee": percentage_fee,
            "fixed_fee": self.fixed_fee,
