from typing import Dict, Optional, List
import datetime
import uuid


class RefundHandler:
    def __init__(self):
        self.refunds = {}
        self.refund_limits = {
            'daily_amount': Decimal('10000'),
            'daily_count': 50,
            'max_age_days': 180
        }

    def process_refund(self, transaction_id: str, original_amount: Decimal, 
                      refund_amount: Decimal, reason: str) -> Dict:
        # Validate refund amount
        if refund_amount <= 0:
            return {"status": "failed", "error": "Invalid refund amount"}

        if refund_amount > original_amount:
            return {"status": "failed", "error": "Refund exceeds original amount"}

        # Check refund limits
        limit_check = self._check_daily_limits(refund_amount)
        if not limit_check['allowed']:
            return {"status": "failed", "error": limit_check['reason']}

        # Create refund record
        refund_id = self._generate_refund_id()
        refund_record = {
            'id': refund_id,
            'transaction_id': transaction_id,
            'original_amount': original_amount,
            'refund_amount': refund_amount,
            'reason': reason,
            'status': 'pending',
            'created_at': datetime.datetime.now(),
            'processed_at': None
        }

        self.refunds[refund_id] = refund_record

        # Process based on amount and reason
        if self._should_auto_approve(refund_amount, reason):
            return self._approve_refund(refund_id)
        else:
            return {
                "status": "pending_review",
                "refund_id": refund_id,
                "message": "Refund requires manual review"
            }

    def _check_daily_limits(self, amount: Decimal) -> Dict:
        today = datetime.date.today()
        daily_total = Decimal('0')
        daily_count = 0

        for refund in self.refunds.values():
            if refund['created_at'].date() == today and refund['status'] != 'rejected':
                daily_total += refund['refund_amount']
                daily_count += 1

        if daily_total + amount > self.refund_limits['daily_amount']:
            return {'allowed': False, 'reason': 'Daily refund amount limit exceeded'}

        if daily_count >= self.refund_limits['daily_count']:
            return {'allowed': False, 'reason': 'Daily refund count limit exceeded'}

        return {'allowed': True}

    def _should_auto_approve(self, amount: Decimal, reason: str) -> bool:
        # Auto-approve small refunds with valid reasons
        if amount <= Decimal('50') and reason in ['duplicate_charge', 'technical_error']:
            return True

        # Auto-approve if customer service tagged
        if reason.startswith('cs_approved_'):
            return True

        return False

    def _generate_refund_id(self) -> str:
        return f"REF{uuid.uuid4().hex[:8].upper()}"

    def _approve_refund(self, refund_id: str) -> Dict:
        if refund_id not in self.refunds:
            return {"status": "failed", "error": "Refund not found"}

        refund = self.refunds[refund_id]
        refund['status'] = 'approved'
        refund['processed_at'] = datetime.datetime.now()

        return {
            "status": "approved",
            "refund_id": refund_id,
            "amount": str(refund['refund_amount']),
            "message": "Refund approved and processing"
        }

    def reject_refund(self, refund_id: str, reason: str) -> Dict:
        if refund_id not in self.refunds:
            return {"status": "failed", "error": "Refund not found"}

        refund = self.refunds[refund_id]
        if refund['status'] != 'pending':
            return {"status": "failed", "error": "Refund already processed"}

        refund['status'] = 'rejected'
        refund['processed_at'] = datetime.datetime.now()
        refund['rejection_reason'] = reason

        return {
            "status": "rejected",
            "refund_id": refund_id,
            "reason": reason
        }

    def get_refund_status(self, refund_id: str) -> Optional[Dict]:
        if refund_id not in self.refunds:
            return None

        refund = self.refunds[refund_id]
        return {
            'refund_id': refund_id,
            'status': refund['status'],
            'amount': str(refund['refund_amount']),
            'created_at': refund['created_at'].isoformat(),
            'processed_at': refund['processed_at'].isoformat() if refund['processed_at'] else None
        }

    def get_pending_refunds(self) -> List[Dict]:
        pending = []
        for refund_id, refund in self.refunds.items():
            if refund['status'] == 'pending':
                pending.append({
                    'refund_id': refund_id,
                    'amount': str(refund['refund_amount']),
                    'reason': refund['reason'],
                    'age_hours': (datetime.datetime.now() - refund['created_at']).total_seconds() / 3600
                })
        return sorted(pending, key=lambda x: x['age_hours'], reverse=True)

    def batch_approve_refunds(self, refund_ids: List[str]) -> Dict:
        approved = []
        failed = []

        for refund_id in refund_ids:
            result = self._approve_refund(refund_id)
            if result['status'] == 'approved':
                approved.append(refund_id)
            else:
                failed.append({'refund_id': refund_id, 'error': result.get('error')})

        return {
            'approved_count': len(approved),
            'failed_count': len(failed),
            'approved': approved,
            'failed': failed
        }

    def get_refund_metrics(self, days: int = 30) -> Dict:
        cutoff_date = datetime.datetime.now() - datetime.timedelta(days=days)

        total_refunds = 0
        total_amount = Decimal('0')
        approved_count = 0
        rejected_count = 0
        pending_count = 0
        avg_processing_time = []

        for refund in self.refunds.values():
            if refund['created_at'] > cutoff_date:
                total_refunds += 1
                total_amount += refund['refund_amount']

                if refund['status'] == 'approved':
                    approved_count += 1
                    if refund['processed_at']:
                        processing_time = (refund['processed_at'] - refund['created_at']).total_seconds() / 3600
                        avg_processing_time.append(processing_time)
                elif refund['status'] == 'rejected':
                    rejected_count += 1
                else:
                    pending_count += 1

        return {
            'period_days': days,
            'total_refunds': total_refunds,
            'total_amount': str(total_amount),
            'approved': approved_count,
            'rejected': rejected_count,
            'pending': pending_count,
            'approval_rate': approved_count / total_refunds if total_refunds > 0 else 0,
            'average_processing_hours': sum(avg_processing_time) / len(avg_processing_time) if avg_processing_time else 0
        }", "tests/test_utils.py": "import pytest
