typedef struct PQueueItem {
    char *value;
    long long priority;
    struct PQueueItem *next;
} PQueueItem;

typedef struct {
    PQueueItem *head;
    size_t size;
} PQueue;

static RedisModuleType *PQueueType;

PQueue *PQueueCreate(void) {
    PQueue *pq = RedisModule_Alloc(sizeof(PQueue));
    pq->head = NULL;
    pq->size = 0;
    return pq;
}

void PQueueFree(PQueue *pq) {
    if (!pq) return;

    PQueueItem *current = pq->head;
    while (current) {
        PQueueItem *next = current->next;
        RedisModule_Free(current->value);
        RedisModule_Free(current);
        current = next;
    }
    RedisModule_Free(pq);
}

