{"elevator_sim.py": "#!/usr/bin/env python3
import sys
from collections import deque
from dataclasses import dataclass
from typing import List, Optional, Tuple

@dataclass
class Request:
    floor: int
    direction: str
    time_requested: int
    time_completed: Optional[int] = None

@dataclass 
class Elevator:
    id: int
    current_floor: int = 1
    direction: str = "IDLE"
    passengers: int = 0
    capacity: int = 10
    in_service: bool = True
    door_timer: int = 0
    destination_floors: List[int] = None

    def __post_init__(self):
        if self.destination_floors is None:
            self.destination_floors = []

class ElevatorSystem:
    def __init__(self, floors: int, elevators: int):
        self.floors = floors
        self.elevators = [Elevator(id=i) for i in range(elevators)]
        self.requests = deque()
        self.time = 0
        self.completed_requests = []

    def process_request(self, floor: int, direction: str):
        if 1 <= floor <= self.floors and direction in ["UP", "DOWN"]:
            req = Request(floor, direction, self.time)
            self.requests.append(req)

    def assign_requests(self):
        # Simple nearest elevator assignment
        for req in list(self.requests):
            best_elevator = None
            min_distance = float('inf')

            for elevator in self.elevators:
                if not elevator.in_service or elevator.passengers >= elevator.capacity:
                    continue

                distance = abs(elevator.current_floor - req.floor)
                if distance < min_distance:
                    min_distance = distance
                    best_elevator = elevator

            if best_elevator and req.floor not in best_elevator.destination_floors:
                best_elevator.destination_floors.append(req.floor)
                self.requests.remove(req)

    def move_elevators(self):
        for elevator in self.elevators:
            if not elevator.in_service:
                continue

            if elevator.door_timer > 0:
                elevator.door_timer -= 1
                continue

            if not elevator.destination_floors:
                elevator.direction = "IDLE"
                continue

            # Move towards next destination
            next_floor = elevator.destination_floors[0]
            if elevator.current_floor < next_floor:
                elevator.current_floor += 1
                elevator.direction = "UP"
            elif elevator.current_floor > next_floor:
                elevator.current_floor -= 1
                elevator.direction = "DOWN"
            else:
                # Arrived at destination
                elevator.destination_floors.pop(0)
                elevator.door_timer = 2

    def tick(self):
        self.time += 1
        self.assign_requests()
        self.move_elevators()

    def get_stats(self):
        wait_times = []
        for req in self.completed_requests:
            if req.time_completed:
                wait_times.append(req.time_completed - req.time_requested)

        avg_wait = sum(wait_times) / len(wait_times) if wait_times else 0
        return avg_wait

    def print_state(self):
        print(f"Time: {self.time}")
        for elevator in self.elevators:
            status = "DOORS" if elevator.door_timer > 0 else elevator.direction
            print(f"Elevator {elevator.id}: Floor {elevator.current_floor}, {status}, Passengers: {elevator.passengers}/{elevator.capacity}")
        print(f"Queue: {len(self.requests)} pending requests")
        print(f"Stats: Avg Wait: {self.get_stats():.1f}")
        print()

def main():
    # 10 floors, 3 elevators
    system = ElevatorSystem(10, 3)

    for line in sys.stdin:
        parts = line.strip().split()
        if not parts:
            continue

        cmd = parts[0]

        if cmd == "REQUEST":
            floor = int(parts[1])
            direction = parts[2]
            system.process_request(floor, direction)
        elif cmd == "TICK":
            system.tick()
        elif cmd == "LOAD":
            elevator_id = int(parts[1])
            passengers = int(parts[2])
            if 0 <= elevator_id < len(system.elevators):
                system.elevators[elevator_id].passengers += passengers
        elif cmd == "UNLOAD":
            elevator_id = int(parts[1])
            passengers = int(parts[2])
            if 0 <= elevator_id < len(system.elevators):
                system.elevators[elevator_id].passengers -= passengers
        elif cmd == "SERVICE":
            elevator_id = int(parts[1])
            status = parts[2] == "on"
            if 0 <= elevator_id < len(system.elevators):
                system.elevators[elevator_id].in_service = status

        system.print_state()

if __name__ == "__main__":
    main()", "test_scenario.txt": "REQUEST 1 UP
