        try:
            return statistics.mode(data)
        except:
            return None

    def _calculate_percentiles(self, data):
        sorted_data = sorted(data)
        return {
            '25': self._percentile(sorted_data, 25),
            '50': self._percentile(sorted_data, 50),
            '75': self._percentile(sorted_data, 75),
            '90': self._percentile(sorted_data, 90),
            '95': self._percentile(sorted_data, 95)
        }

    def _percentile(self, sorted_data, p):
        k = (len(sorted_data) - 1) * p / 100
        f = int(k)
        c = k - f
        if f + 1 < len(sorted_data):
            return sorted_data[f] + c * (sorted_data[f + 1] - sorted_data[f])
        else:
            return sorted_data[f]

    def _find_outliers(self, data):
        q1 = self._percentile(sorted(data), 25)
        q3 = self._percentile(sorted(data), 75)
        iqr = q3 - q1
        lower_bound = q1 - 1.5 * iqr
        upper_bound = q3 + 1.5 * iqr
        return [x for x in data if x < lower_bound or x > upper_bound]

