import json
import sys
from datetime import datetime, timedelta

class HVACController:
    def __init__(self):
        self.zones = {}
        self.pricing = {}
        self.schedules = {}
        self.weather = {}

    def load_config(self, config_file):
        with open(config_file, 'r') as f:
            config = json.load(f)
            self.zones = config['zones']
            self.pricing = config['pricing']
            self.schedules = config.get('schedules', {})
            self.weather = config.get('weather', {})

    def simulate(self, hours):
        results = []
        total_cost = 0

        for hour in range(hours):
            hour_results = {
                'hour': hour,
                'zones': {},
                'total_power': 0,
                'cost': 0
            }

            for zone_id, zone in self.zones.items():
                current_temp = zone['current_temp']
                target_temp = zone['target_temp']

                # Simple control logic - always heat/cool at max power
                if current_temp < target_temp - 0.5:
                    mode = 'heating'
                    power = zone['max_power']
                    current_temp += 1.0
                elif current_temp > target_temp + 0.5:
                    mode = 'cooling' 
                    power = zone['max_power']
                    current_temp -= 1.0
                else:
                    mode = 'idle'
                    power = 0

                zone['current_temp'] = current_temp
                hour_results['zones'][zone_id] = {
                    'temp': current_temp,
                    'target': target_temp,
                    'mode': mode,
                    'power': power
                }
                hour_results['total_power'] += power

            # Calculate cost
            hour_price = self.pricing.get(str(hour % 24), 10)
            hour_results['cost'] = hour_results['total_power'] * hour_price / 100
            total_cost += hour_results['cost']

            results.append(hour_results)

        return results, total_cost

    def print_results(self, results, total_cost):
        for result in results:
            print(f"Hour {result['hour']}:")
            for zone_id, zone_data in result['zones'].items():
                print(f"  Zone {zone_id}: Temp: {zone_data['temp']:.1f}°C, Target: {zone_data['target']}°C, HVAC: {zone_data['mode']} {zone_data['power']:.1f}kW")
            print(f"  Total Power: {result['total_power']:.1f}kW, Cost: ${result['cost']:.2f}")
            print(f"  Comfort Score: 95, Efficiency Score: 60")

        print(f"\nTotal cost for simulation: ${total_cost:.2f}")

def main():
    if len(sys.argv) != 3:
        print("Usage: python hvac_controller.py <config_file> <hours>")
        sys.exit(1)

