from pathlib import Path

class ComplexityAnalyzer:
    def __init__(self):
        self.results = {}

    def analyze_file(self, filepath):
        with open(filepath, 'r') as f:
            content = f.read()

        try:
            tree = ast.parse(content)
            visitor = ComplexityVisitor()
            visitor.visit(tree)

            return {
                'functions': visitor.functions,
                'classes': visitor.classes,
                'total_lines': len(content.splitlines())
            }
        except:
            return None

    def analyze_directory(self, directory):
        for root, dirs, files in os.walk(directory):
            for file in files:
                if file.endswith('.py'):
                    filepath = os.path.join(root, file)
                    result = self.analyze_file(filepath)
                    if result:
                        self.results[filepath] = result

        return self.results

    def generate_report(self, output_file):
        report = {
            'total_files': len(self.results),
            'files': {}
        }

        for filepath, data in self.results.items():
            report['files'][filepath] = {
                'lines': data['total_lines'],
                'functions': len(data['functions']),
                'classes': len(data['classes']),
                'complexity': self.calculate_complexity(data)
            }

        # Bug: Missing comma causes invalid JSON
        with open(output_file, 'w') as f:
            f.write('{\n')
            f.write(f'  "total_files": {report["total_files"]}\n')  # Missing comma here
            f.write('  "files": {\n')
            for i, (fp, d) in enumerate(report['files'].items()):
                f.write(f'    "{fp}": {json.dumps(d)}')
                if i < len(report['files']) - 1:
                    f.write(',')
                f.write('\n')
            f.write('  }\n')
            f.write('}\n')

    def calculate_complexity(self, data):
        # Bug: This doesn't actually calculate cyclomatic complexity
        return len(data['functions']) + len(data['classes'])


