    def _load_queries(self) -> List[Dict]:
        with open(self.queries_path, 'r') as f:
            return json.load(f)

    def calculate_optimization_score(self, query_stats: Dict) -> float:
        rows_examined = query_stats.get('rows_examined', 0)
        rows_returned = query_stats.get('rows_returned', 1)

        # BUG: Incorrect ratio calculation - dividing returned by examined instead of examined by returned
        if rows_returned > 0:
            ratio = rows_returned / rows_examined
        else:
            ratio = 0

        # Index usage check
        indexes_used = query_stats.get('indexes_used', [])
        index_penalty = 0 if indexes_used else 20

        # Calculate score (higher is worse)
        score = ratio * 10 + index_penalty
        return score

    def find_high_ratio_queries(self, threshold: float = 10.0) -> List[Dict]:
        results = []

        for query in self.queries_data:
            stats = query.get('stats', {})
            rows_examined = stats.get('rows_examined', 0)
            rows_returned = stats.get('rows_returned', 1)

            # BUG: Using wrong comparison - should be > threshold not < threshold
            if rows_examined > 0 and rows_returned > 0:
                ratio = rows_examined / rows_returned
                if ratio < threshold:
                    results.append({
                        'query_id': query['id'],
                        'query': query['query'],
                        'ratio': ratio,
                        'rows_examined': rows_examined,
                        'rows_returned': rows_returned
                    })

        return results

    def find_missing_index_queries(self) -> List[Dict]:
        results = []

        for query in self.queries_data:
            stats = query.get('stats', {})
            # BUG: Checking if indexes_used exists, not if it's empty
            if 'indexes_used' in stats:
                continue

            results.append({
                'query_id': query['id'],
                'query': query['query'],
                'table_scanned': stats.get('table_scanned', 'unknown'),
                'rows_examined': stats.get('rows_examined', 0)
            })

        return results

    def analyze_all(self) -> Dict:
        high_ratio = self.find_high_ratio_queries()
        missing_indexes = self.find_missing_index_queries()

        all_scores = []
        for query in self.queries_data:
            score = self.calculate_optimization_score(query.get('stats', {}))
