import numpy as np
from datetime import datetime, timedelta
from collections import deque
import time

class StreamProcessor:
    def __init__(self, window_size_seconds=60, slide_interval_seconds=10):
        self.window_size = window_size_seconds
        self.slide_interval = slide_interval_seconds
        self.windows = {}
        self.processed_count = 0

    def process_record(self, timestamp, sensor_id, value):
        # BUG: Using current time instead of event timestamp for window assignment
        current_time = datetime.now()

        # Calculate which windows this record belongs to
        window_start = current_time - timedelta(seconds=current_time.timestamp() % self.slide_interval)

        # Add to all applicable windows
        for i in range(int(self.window_size / self.slide_interval)):
            window_key = window_start - timedelta(seconds=i * self.slide_interval)
            if window_key not in self.windows:
                self.windows[window_key] = []

            # Only keep data within window
            window_end = window_key + timedelta(seconds=self.window_size)
            if current_time <= window_end:
                self.windows[window_key].append({
                    'timestamp': timestamp,
                    'sensor_id': sensor_id,
                    'value': value
                })

        self.processed_count += 1

        # Clean old windows
        cutoff = current_time - timedelta(seconds=self.window_size * 2)
        self.windows = {k: v for k, v in self.windows.items() if k > cutoff}

    def get_window_aggregates(self):
        results = []
        for window_start, records in self.windows.items():
            if records:
                window_end = window_start + timedelta(seconds=self.window_size)
                avg_value = np.mean([r['value'] for r in records])
                results.append({
                    'window_start': window_start,
                    'window_end': window_end,
                    'avg_value': avg_value,
                    'count': len(records)
                })
        return sorted(results, key=lambda x: x['window_start'])

    def process_batch(self, df):
        for _, row in df.iterrows():
            self.process_record(row['timestamp'], row['sensor_id'], row['value'])
            # Simulate streaming delay
            time.sleep(0.001)

        return self.get_window_aggregates()

def calculate_ground_truth(df, window_size_seconds=60, slide_interval_seconds=10):
    df = df.copy()
    df['timestamp'] = pd.to_datetime(df['timestamp'])
    df = df.sort_values('timestamp')

    results = []
    start_time = df['timestamp'].min()
    end_time = df['timestamp'].max()

    current_window = start_time
    while current_window <= end_time:
        window_end = current_window + timedelta(seconds=window_size_seconds)
        window_data = df[(df['timestamp'] >= current_window) & (df['timestamp'] < window_end)]

        if not window_data.empty:
            results.append({
                'window_start': current_window,
                'window_end': window_end,
                'avg_value': window_data['value'].mean(),
                'count': len(window_data)
            })

        current_window += timedelta(seconds=slide_interval_seconds)

