import numpy as np
from datetime import datetime, timedelta
from stream_processor import StreamProcessor, calculate_ground_truth

def generate_test_data():
    # Generate synthetic sensor data
    np.random.seed(42)
    start_time = datetime.now() - timedelta(minutes=10)

    data = []
    for i in range(1000):
        timestamp = start_time + timedelta(seconds=i * 0.5)
        sensor_id = f"sensor_{i % 5}"
        value = 50 + 10 * np.sin(i / 100) + np.random.normal(0, 2)
        data.append({
            'timestamp': timestamp,
            'sensor_id': sensor_id,
            'value': value
        })

    df = pd.DataFrame(data)
    df.to_csv('sensor_data.csv', index=False)
    return df

def calculate_accuracy(streaming_results, ground_truth):
    if not streaming_results or not ground_truth:
        return 0.0

    # Match windows and calculate accuracy
    matches = 0
    total = 0

    for gt in ground_truth:
        for sr in streaming_results:
            # Find matching window (within 1 second tolerance)
            if abs((sr['window_start'] - gt['window_start']).total_seconds()) < 1:
                # Check if aggregated values are close
                if abs(sr['avg_value'] - gt['avg_value']) < 0.1:
                    matches += 1
                total += 1
                break

    if total == 0:
        return 0.0

    return (matches / total) * 100

def main():
    print("Generating test data...")
    df = generate_test_data()

    print("Processing stream...")
    processor = StreamProcessor(window_size_seconds=60, slide_interval_seconds=10)
    streaming_results = processor.process_batch(df)

    print("Calculating ground truth...")
    ground_truth = calculate_ground_truth(df, window_size_seconds=60, slide_interval_seconds=10)

    accuracy = calculate_accuracy(streaming_results, ground_truth)
    print(f"\nWindow Aggregation Accuracy: {accuracy:.1f}%")

    if accuracy > 99:
        print("✓ Accuracy target achieved!")
    else:
        print("✗ Accuracy below target (need >99%)")

if __name__ == "__main__":
    main()"}

