import (
	"fmt"
	"strings"
	"syscall"
)

func showFilesystemStats() {
	var stat syscall.Statfs_t

	paths := []string{"/", "/tmp", "/var", "/home"}

	fmt.Printf("%-20s %-15s %-15s %-15s %-10s\n", "MOUNT", "TOTAL", "USED", "AVAILABLE", "USE%")
	fmt.Println(strings.Repeat("-", 75))

	for _, path := range paths {
		err := syscall.Statfs(path, &stat)
		if err != nil {
			continue
		}

		total := stat.Blocks * uint64(stat.Bsize)
		free := stat.Bavail * uint64(stat.Bsize)
		used := total - free

		totalMB := total / (1024 * 1024)
		usedMB := used / (1024 * 1024)
		freeMB := free / (1024 * 1024)

		usePercent := 0
		if total > 0 {
			usePercent = int((used * 100) / total)
		}

		fmt.Printf("%-20s %-15s %-15s %-15s %-10s\n",
			path,
			fmt.Sprintf("%d MB", totalMB),
			fmt.Sprintf("%d MB", usedMB),
			fmt.Sprintf("%d MB", freeMB),
			fmt.Sprintf("%d%%", usePercent))
	}
