import (
	"fmt"
	"io/ioutil"
	"strconv"
	"strings"
)

func listProcesses() {
	fmt.Printf("%-10s %-20s %-10s %-10s\n", "PID", "NAME", "STATE", "MEMORY")
	fmt.Println(strings.Repeat("-", 55))

	dirs, err := ioutil.ReadDir("/proc")
	if err != nil {
		fmt.Printf("Error reading /proc: %v\n", err)
		return
	}

	count := 0
	for _, dir := range dirs {
		if !dir.IsDir() {
			continue
		}

		pid, err := strconv.Atoi(dir.Name())
		if err != nil {
			continue
		}

		// Read process info
		statPath := fmt.Sprintf("/proc/%d/stat", pid)
		statData, err := ioutil.ReadFile(statPath)
		if err != nil {
			continue
		}

		// Parse basic info (simplified)
		parts := strings.Split(string(statData), " ")
		if len(parts) < 3 {
			continue
		}

		name := strings.Trim(parts[1], "()")
		state := parts[2]

		// Read memory info
		statusPath := fmt.Sprintf("/proc/%d/status", pid)
		statusData, err := ioutil.ReadFile(statusPath)
		if err != nil {
			continue
		}

		memory := "N/A"
		for _, line := range strings.Split(string(statusData), "\n") {
			if strings.HasPrefix(line, "VmRSS:") {
				parts := strings.Fields(line)
				if len(parts) >= 2 {
					memory = parts[1] + " " + parts[2]
				}
				break
			}
		}

		fmt.Printf("%-10d %-20s %-10s %-10s\n", pid, name, state, memory)
		count++
		if count >= 20 {
			fmt.Println("... (showing first 20 processes)")
			break
		}
	}
