func main() {
	fmt.Println("Debug Shell (dsh) v0.1")
	fmt.Println("Type 'help' for commands")

	scanner := bufio.NewScanner(os.Stdin)
	for {
		fmt.Print("dsh> ")
		if !scanner.Scan() {
			break
		}

		line := scanner.Text()
		parts := strings.Fields(line)
		if len(parts) == 0 {
			continue
		}

		switch parts[0] {
		case "help":
			showHelp()
		case "ps":
			listProcesses()
		case "net":
			fmt.Println("Network inspection not implemented")
		case "fs":
			showFilesystemStats()
		case "exit", "quit":
			fmt.Println("Goodbye!")
			return
		default:
			fmt.Printf("Unknown command: %s\n", parts[0])
