import numpy as np
from flask import Flask, jsonify
import time

app = Flask(__name__)

class ThompsonSamplingOptimizer:
    def __init__(self, n_ads):
        self.n_ads = n_ads
        self.alpha = np.ones(n_ads)  # successes + 1
        self.beta = np.ones(n_ads)   # failures + 1
        self.impressions = []

    def select_ad(self):
        # Thompson Sampling: sample from Beta distributions
        samples = [np.random.beta(self.alpha[i], self.beta[i]) for i in range(self.n_ads)]
        return np.argmax(samples)

    def update(self, ad_id, clicked):
        impression = {
            'ad_id': ad_id,
            'clicked': clicked,
            'timestamp': time.time()
        }
        self.impressions.append(impression)

        if clicked:
            self.alpha[ad_id] += 1
        else:
            self.beta[ad_id] += 1

    def get_analytics(self):
        # Slow analytics computation
        analytics = {
            'total_impressions': len(self.impressions),
            'ads': []
        }

        for ad_id in range(self.n_ads):
            ad_impressions = [imp for imp in self.impressions if imp['ad_id'] == ad_id]
            clicks = sum(1 for imp in ad_impressions if imp['clicked'])

            ad_stats = {
                'ad_id': ad_id,
                'impressions': len(ad_impressions),
                'clicks': clicks,
                'ctr': clicks / len(ad_impressions) if ad_impressions else 0,
                'estimated_ctr': self.alpha[ad_id] / (self.alpha[ad_id] + self.beta[ad_id])
            }
            analytics['ads'].append(ad_stats)

        # Simulate expensive computation
        time.sleep(0.001 * len(self.impressions) / 100)  # Gets slower with more data

        return analytics

# Initialize optimizer
optimizer = ThompsonSamplingOptimizer(n_ads=5)

# Load sample data
with open('sample_data.json', 'r') as f:
    sample_data = json.load(f)
    for impression in sample_data['impressions']:
        optimizer.update(impression['ad_id'], impression['clicked'])

@app.route('/select_ad')
def select_ad():
    ad_id = optimizer.select_ad()
    return jsonify({'ad_id': ad_id})

@app.route('/record_click/<int:ad_id>/<int:clicked>')
def record_click(ad_id, clicked):
    optimizer.update(ad_id, bool(clicked))
    return jsonify({'success': True})

@app.route('/analytics')
def get_analytics():
    start_time = time.time()
    analytics = optimizer.get_analytics()
    analytics['computation_time'] = time.time() - start_time
    return jsonify(analytics)

if __name__ == '__main__':
