import random
from typing import List, Optional, Tuple

class ConnectFour:
    def __init__(self):
        self.board = np.zeros((6, 7), dtype=int)
        self.current_player = 1

    def make_move(self, col: int) -> bool:
        if col < 0 or col >= 7 or self.board[0][col] != 0:
            return False

        for row in range(5, -1, -1):
            if self.board[row][col] == 0:
                self.board[row][col] = self.current_player
                self.current_player = 3 - self.current_player
                return True
        return False

    def get_valid_moves(self) -> List[int]:
        return [col for col in range(7) if self.board[0][col] == 0]

    def check_winner(self) -> int:
        # Check horizontal
        for row in range(6):
            for col in range(4):
                if self.board[row][col] != 0:
                    if all(self.board[row][col+i] == self.board[row][col] for i in range(4)):
                        return self.board[row][col]

        # Check vertical
        for row in range(3):
            for col in range(7):
                if self.board[row][col] != 0:
                    if all(self.board[row+i][col] == self.board[row][col] for i in range(4)):
                        return self.board[row][col]

        # Check diagonal (positive slope)
        for row in range(3):
            for col in range(4):
                if self.board[row][col] != 0:
                    if all(self.board[row+i][col+i] == self.board[row][col] for i in range(4)):
                        return self.board[row][col]

        # Check diagonal (negative slope)
        for row in range(3, 6):
            for col in range(4):
                if self.board[row][col] != 0:
                    if all(self.board[row-i][col+i] == self.board[row][col] for i in range(4)):
                        return self.board[row][col]

        # Check draw
        if len(self.get_valid_moves()) == 0:
            return -1

        return 0

    def copy(self):
        new_game = ConnectFour()
        new_game.board = self.board.copy()
        new_game.current_player = self.current_player
        return new_game

class MinimaxPlayer:
    """Current slow minimax implementation"""
    def __init__(self, depth=4):
        self.depth = depth

    def get_move(self, game: ConnectFour) -> int:
        _, move = self.minimax(game, self.depth, True, float('-inf'), float('inf'))
        return move

    def minimax(self, game: ConnectFour, depth: int, maximizing: bool, alpha: float, beta: float) -> Tuple[float, int]:
        winner = game.check_winner()
        if winner != 0 or depth == 0:
            return self.evaluate(game, winner), -1

        best_move = -1
        if maximizing:
            max_eval = float('-inf')
            for move in game.get_valid_moves():
                game_copy = game.copy()
                game_copy.make_move(move)
                eval_score, _ = self.minimax(game_copy, depth-1, False, alpha, beta)
                if eval_score > max_eval:
                    max_eval = eval_score
                    best_move = move
                alpha = max(alpha, eval_score)
                if beta <= alpha:
                    break
            return max_eval, best_move
        else:
            min_eval = float('inf')
            for move in game.get_valid_moves():
                game_copy = game.copy()
                game_copy.make_move(move)
                eval_score, _ = self.minimax(game_copy, depth-1, True, alpha, beta)
                if eval_score < min_eval:
                    min_eval = eval_score
                    best_move = move
                beta = min(beta, eval_score)
                if beta <= alpha:
                    break
            return min_eval, best_move

    def evaluate(self, game: ConnectFour, winner: int) -> float:
        if winner == 1:
            return 1000
        elif winner == 2:
            return -1000
        elif winner == -1:
            return 0
        return 0

class RandomPlayer:
    def get_move(self, game: ConnectFour) -> int:
        moves = game.get_valid_moves()
        return random.choice(moves) if moves else -1

def play_game(player1, player2, display=False):
    game = ConnectFour()
    players = [player1, player2]

    while True:
        if display:
            print(game.board)
            print()

        current_player_idx = 0 if game.current_player == 1 else 1
        move = players[current_player_idx].get_move(game)

        if move == -1 or not game.make_move(move):
            break

        winner = game.check_winner()
        if winner != 0:
            if display:
                print(f"Game Over! Winner: {winner}")
            return winner

    return -1"}

