import socket
import threading
import json
import time
from warehouse_grid import WarehouseGrid

class Coordinator:
    def __init__(self, port=9999):
        self.port = port
        self.grid = WarehouseGrid(10, 10)
        self.robots = {}
        self.lock = threading.Lock()
        self.collision_log = []
        self.delivery_log = []

    def start(self):
        server = socket.socket(socket.AF_INET, socket.SOCK_STREAM)
        server.setsockopt(socket.SOL_SOCKET, socket.SO_REUSEADDR, 1)
        server.bind(('localhost', self.port))
        server.listen(5)
        print(f"Coordinator listening on port {self.port}")

        while True:
            client, addr = server.accept()
            thread = threading.Thread(target=self.handle_robot, args=(client,))
            thread.daemon = True
            thread.start()

    def handle_robot(self, client):
        robot_id = None
        try:
            while True:
                data = client.recv(1024).decode()
                if not data:
                    break

                request = json.loads(data)
                cmd = request['command']

                if cmd == 'register':
                    robot_id = request['robot_id']
                    pos = request['position']
                    with self.lock:
                        self.robots[robot_id] = {
                            'position': pos,
                            'destination': None,
                            'path': []
                        }
                    response = {'status': 'registered'}

                elif cmd == 'move':
                    robot_id = request['robot_id']
                    new_pos = request['position']

                    collision = False
                    with self.lock:
                        for rid, robot in self.robots.items():
                            if rid != robot_id and robot['position'] == new_pos:
                                collision = True
                                break

                    if collision:
                        response = {'status': 'collision', 'allowed': False}
                    else:
                        with self.lock:
                            old_pos = self.robots[robot_id]['position']
                            self.robots[robot_id]['position'] = new_pos

                            # Check if collision actually happened (robots swapped positions)
                            for rid, robot in self.robots.items():
                                if rid != robot_id and robot['position'] == old_pos:
                                    # Two robots swapped positions - this is a collision!
                                    self.collision_log.append({
                                        'time': time.time(),
                                        'robots': [robot_id, rid],
                                        'positions': [new_pos, old_pos]
                                    })

                        response = {'status': 'moved', 'allowed': True}

                elif cmd == 'delivery':
                    robot_id = request['robot_id']
                    with self.lock:
                        self.delivery_log.append({
                            'robot_id': robot_id,
                            'time': time.time(),
                            'position': self.robots[robot_id]['position']
                        })
                    response = {'status': 'delivered'}

                elif cmd == 'status':
                    with self.lock:
                        response = {
                            'robots': self.robots.copy(),
                            'collisions': len(self.collision_log),
                            'deliveries': len(self.delivery_log)
                        }

                client.send(json.dumps(response).encode())

        except Exception as e:
            print(f"Error handling robot {robot_id}: {e}")
        finally:
            if robot_id:
                with self.lock:
                    if robot_id in self.robots:
                        del self.robots[robot_id]
            client.close()

if __name__ == '__main__':
    coordinator = Coordinator()
    coordinator.start()", "warehouse_grid.py": "#!/usr/bin/env python3

