import socket
import json
import random
import time
import sys

class Robot:
    def __init__(self, robot_id, coordinator_host='localhost', coordinator_port=9999):
        self.robot_id = robot_id
        self.host = coordinator_host
        self.port = coordinator_port
        self.position = self._random_start_position()
        self.deliveries = 0
        self.moves = 0
        self.socket = None

    def _random_start_position(self):
        return (random.randint(0, 9), random.randint(0, 9))

    def connect(self):
        self.socket = socket.socket(socket.AF_INET, socket.SOCK_STREAM)
        self.socket.connect((self.host, self.port))

        # Register with coordinator
        request = {
            'command': 'register',
            'robot_id': self.robot_id,
            'position': self.position
        }
        self._send_request(request)

    def _send_request(self, request):
        self.socket.send(json.dumps(request).encode())
        response = self.socket.recv(1024).decode()
        return json.loads(response)

    def move_to(self, target):
        """Simple pathfinding - move one step towards target"""
        x, y = self.position
        tx, ty = target

        # Determine next position
        if x < tx:
            next_pos = (x + 1, y)
        elif x > tx:
            next_pos = (x - 1, y)
        elif y < ty:
            next_pos = (x, y + 1)
        elif y > ty:
            next_pos = (x, y - 1)
        else:
            return True  # Already at target

        # Request move from coordinator
        request = {
            'command': 'move',
            'robot_id': self.robot_id,
            'position': next_pos
        }
        response = self._send_request(request)

        if response['allowed']:
            self.position = next_pos
            self.moves += 1

        return self.position == target

    def deliver(self):
        request = {
            'command': 'delivery',
            'robot_id': self.robot_id
        }
        self._send_request(request)
        self.deliveries += 1

    def run_deliveries(self, num_deliveries=5):
        delivery_points = [
            (0, 0), (9, 0), (0, 9), (9, 9),
            (4, 4), (5, 5), (4, 5), (5, 4)
        ]

        for i in range(num_deliveries):
            target = random.choice(delivery_points)

            # Move to delivery point
            while not self.move_to(target):
                time.sleep(0.1)

            # Make delivery
            self.deliver()
            print(f"Robot {self.robot_id}: Delivered at {self.position} ({self.deliveries} total)")

        self.socket.close()

if __name__ == '__main__':
    if len(sys.argv) < 2:
        print("Usage: python robot.py <robot_id>")
        sys.exit(1)

    robot = Robot(sys.argv[1])
    robot.connect()
    robot.run_deliveries()", "test_robots.sh": "#!/bin/bash

