import json
import time
import random
from collections import deque

class TaskQueue:
    def __init__(self):
        self.pending_tasks = deque()
        self.worker_queues = {f"worker-{i}": deque() for i in range(1, 9)}
        self.completed_tasks = []
        self.failed_tasks = []
        self.task_assignments = {}  # task_id -> worker_id

    def add_task(self, task_id, complexity=1):
        task = {
            "id": task_id,
            "complexity": complexity,
            "created_at": time.time(),
            "status": "pending"
        }
        self.pending_tasks.append(task)

    def assign_task_to_worker(self, task_id, worker_id):
        # Find task in pending
        task = None
        for t in list(self.pending_tasks):
            if t["id"] == task_id:
                task = t
                self.pending_tasks.remove(t)
                break

        if task:
            task["assigned_to"] = worker_id
            task["assigned_at"] = time.time()
            task["status"] = "assigned"
            self.worker_queues[worker_id].append(task)
            self.task_assignments[task_id] = worker_id
            return True
        return False

    def get_pending_tasks(self):
        return list(self.pending_tasks)

    def get_worker_queue_depth(self, worker_id):
        return len(self.worker_queues.get(worker_id, []))

    def complete_task(self, task_id):
        # Find and remove from worker queue
        for worker_id, queue in self.worker_queues.items():
            for task in list(queue):
                if task["id"] == task_id:
                    queue.remove(task)
                    task["completed_at"] = time.time()
                    task["status"] = "completed"
                    self.completed_tasks.append(task)
