        post_data = self.rfile.read(content_length)
        data = json.loads(post_data)

        if self.path == '/move':
            result = warehouse.move_robot(data['robot_id'], tuple(data['position']))
        elif self.path == '/pickup':
            result = warehouse.pickup_item(data['robot_id'], data['task_id'])
        elif self.path == '/deliver':
            result = warehouse.deliver_item(data['robot_id'])
        elif self.path == '/add_task':
            task_id = warehouse.add_task(
                tuple(data['pickup']),
                tuple(data['dropoff']),
                data.get('priority', 1)
            )
            result = {'success': True, 'task_id': task_id}
        else:
            self.send_error(404)
            return

        self.send_response(200)
        self.send_header('Content-type', 'application/json')
        self.end_headers()
        self.wfile.write(json.dumps(result).encode())

