app = Flask(__name__)

def ip_to_int(ip_str):
    """Convert IP address string to integer using binary operations"""
    octets = ip_str.split('.')
    if len(octets) != 4:
        raise ValueError("Invalid IP address format")

    result = 0
    for octet in octets:
        octet_int = int(octet)
        if octet_int < 0 or octet_int > 255:
            raise ValueError("Invalid octet value")
        result = (result << 8) | octet_int
    return result

def int_to_ip(ip_int):
    """Convert integer to IP address string using binary operations"""
    octets = []
    for _ in range(4):
        octets.append(str(ip_int & 0xFF))
        ip_int >>= 8
    return '.'.join(reversed(octets))

def parse_cidr(cidr):
    """Parse CIDR notation and return IP and prefix length"""
    parts = cidr.split('/')
    if len(parts) != 2:
        raise ValueError("Invalid CIDR notation")

    ip_str = parts[0]
    try:
        prefix_len = int(parts[1])
        if prefix_len < 0 or prefix_len > 32:
            raise ValueError("Invalid prefix length")
    except ValueError:
        raise ValueError("Invalid prefix length")

    return ip_str, prefix_len

def calculate_subnet_info(cidr):
    """Calculate subnet information using binary math"""
    ip_str, prefix_len = parse_cidr(cidr)
    ip_int = ip_to_int(ip_str)

    # Calculate subnet mask using binary operations
    if prefix_len == 0:
        subnet_mask = 0
    else:
        subnet_mask = (0xFFFFFFFF << (32 - prefix_len)) & 0xFFFFFFFF

    # Calculate network address
    network_int = ip_int & subnet_mask

    # Calculate broadcast address
    host_bits = 32 - prefix_len
    if host_bits == 32:
        broadcast_int = 0xFFFFFFFF
    else:
        broadcast_int = network_int | ((1 << host_bits) - 1)

    # Calculate first and last usable IPs
    if prefix_len >= 31:
        # For /31 and /32, no traditional usable hosts
        first_usable_int = network_int
        last_usable_int = broadcast_int
        usable_hosts = 0
    else:
        first_usable_int = network_int + 1
        last_usable_int = broadcast_int - 1
        usable_hosts = (1 << host_bits) - 2

    return {
        'network': int_to_ip(network_int),
        'broadcast': int_to_ip(broadcast_int),
        'first_usable': int_to_ip(first_usable_int),
        'last_usable': int_to_ip(last_usable_int),
        'subnet_mask': int_to_ip(subnet_mask),
        'usable_hosts': usable_hosts
    }

@app.route('/health')
