    """Parse chemical formula and return element counts."""
    def parse_group(formula, start=0):
        elements = {}
        i = start

        while i < len(formula):
            if formula[i] == '(':
                # Find matching closing parenthesis
                level = 1
                j = i + 1
                while j < len(formula) and level > 0:
                    if formula[j] == '(':
                        level += 1
                    elif formula[j] == ')':
                        level -= 1
                    j += 1

                # Parse the group inside parentheses
                group_elements = parse_group(formula, i + 1)[0]

                # Get the multiplier after the closing parenthesis
                i = j
                count = ''
                while i < len(formula) and formula[i].isdigit():
                    count += formula[i]
                    i += 1
                count = int(count) if count else 1

                # Add elements from the group
                for element, element_count in group_elements.items():
                    elements[element] = elements.get(element, 0) + element_count * count

            elif formula[i] == ')':
                # End of current group
