import paho.mqtt.client as mqtt
import json
import time
from collections import defaultdict, deque
from datetime import datetime
import threading

class WeatherDataStore:
    def __init__(self, history_size=100):
        self.stations = defaultdict(lambda: {"current": None, "history": deque(maxlen=history_size)})
        self.lock = threading.Lock()

    def add_reading(self, station_id, data):
        with self.lock:
            self.stations[station_id]["current"] = data
            self.stations[station_id]["history"].append(data)

    def get_stations(self):
        with self.lock:
            return list(self.stations.keys())

    def get_current(self, station_id):
        with self.lock:
            if station_id in self.stations:
                return self.stations[station_id]["current"]
            return None

    def get_history(self, station_id, limit=10):
        with self.lock:
            if station_id in self.stations:
                history = list(self.stations[station_id]["history"])
                return history[-limit:] if len(history) > limit else history
            return None

class MQTTWeatherBridge:
    def __init__(self):
        self.data_store = WeatherDataStore()
        self.mqtt_connected = False
        self.client = mqtt.Client()
        self.setup_mqtt()

    def setup_mqtt(self):
        self.client.on_connect = self.on_connect
        self.client.on_message = self.on_message
        self.client.on_disconnect = self.on_disconnect

    def on_connect(self, client, userdata, flags, rc):
        if rc == 0:
            print("Connected to MQTT broker")
            self.mqtt_connected = True
            client.subscribe("sensors/+/weather")
        else:
            print(f"Failed to connect to MQTT broker: {rc}")
            self.mqtt_connected = False

    def on_disconnect(self, client, userdata, rc):
        print("Disconnected from MQTT broker")
        self.mqtt_connected = False

    def on_message(self, client, userdata, msg):
        try:
            topic_parts = msg.topic.split('/')
            if len(topic_parts) == 3 and topic_parts[0] == "sensors" and topic_parts[2] == "weather":
                station_id = topic_parts[1]
                data = json.loads(msg.payload.decode())
                self.data_store.add_reading(station_id, data)
        except Exception as e:
            print(f"Error processing message: {e}")

    def connect(self, host="localhost", port=1883):
        try:
            self.client.connect(host, port, 60)
            self.client.loop_start()
            return True
        except Exception as e:
            print(f"Error connecting to MQTT broker: {e}")
            return False

    def disconnect(self):
        self.client.loop_stop()
        self.client.disconnect()

    def is_connected(self):
        return self.mqtt_connected"}

