from flask import Flask, jsonify, request
import requests
import time
import threading

app = Flask(__name__)

metrics_store = {}
exporters = [
    'http://localhost:9100/metrics',
    'http://localhost:9101/metrics', 
    'http://localhost:9102/metrics'
]

def parse_prometheus_metrics(text):
    metrics = {}
    for line in text.strip().split('\n'):
        if line.startswith('#') or not line:
            continue

        parts = line.split(' ')
        if len(parts) == 2:
            metric_name = parts[0].split('{')[0]
            value = float(parts[1])
            metrics[metric_name] = value

    return metrics

def scrape_exporters():
    while True:
        for exporter_url in exporters:
            try:
                response = requests.get(exporter_url, timeout=5)
                if response.status_code == 200:
                    parsed = parse_prometheus_metrics(response.text)
                    metrics_store[exporter_url] = {
                        'timestamp': time.time(),
                        'metrics': parsed
                    }
            except:
                pass

        time.sleep(10)

@app.route('/metrics/current')
def current_metrics():
    all_metrics = {}

    for exporter, data in metrics_store.items():
        for metric_name, value in data['metrics'].items():
            if metric_name not in all_metrics:
                all_metrics[metric_name] = []
            all_metrics[metric_name].append(value)

    return jsonify({
        'status': 'ok',
        'metrics': all_metrics,
        'timestamp': time.time()
    })

if __name__ == '__main__':
    scraper_thread = threading.Thread(target=scrape_exporters)
    scraper_thread.daemon = True
    scraper_thread.start()

