import numpy as np
import struct

# Generate simple test weights that will classify digits 0-4
np.random.seed(42)

# CNN architecture parameters
CONV1_FILTERS = 8
CONV1_SIZE = 3
FC_HIDDEN = 32
NUM_CLASSES = 10

# Generate weights in Q8.8 fixed-point format
def to_fixed(x, scale=256):
    return np.clip(x * scale, -32768, 32767).astype(np.int16)

# Design conv filters to detect specific patterns for each digit
conv1_weights = np.zeros((CONV1_FILTERS, CONV1_SIZE, CONV1_SIZE))

# Filter 0: Detect filled squares (for digit 0)
conv1_weights[0] = [[0.2, 0.2, 0.2], 
                    [0.2, 0.3, 0.2], 
                    [0.2, 0.2, 0.2]]

# Filter 1: Detect vertical lines (for digit 1)  
conv1_weights[1] = [[0.1, 0.5, 0.1],
                    [0.1, 0.5, 0.1],
                    [0.1, 0.5, 0.1]]

# Filter 2: Detect horizontal lines (for digits 2, 3)
conv1_weights[2] = [[0.1, 0.1, 0.1],
                    [0.5, 0.5, 0.5],
                    [0.1, 0.1, 0.1]]

# Filter 3: Detect corners (for digit 4)
conv1_weights[3] = [[0.3, 0.1, 0.1],
                    [0.3, 0.3, 0.1],
                    [0.1, 0.1, 0.1]]

# Remaining filters with small random values
for i in range(4, CONV1_FILTERS):
    conv1_weights[i] = np.random.randn(CONV1_SIZE, CONV1_SIZE) * 0.05

conv1_weights = to_fixed(conv1_weights)
conv1_bias = to_fixed(np.ones(CONV1_FILTERS) * 0.1)

# FC1 weights - connect specific filter responses to hidden units
fc1_weights = np.zeros((FC_HIDDEN, 13*13*CONV1_FILTERS))

# Set up connections to amplify digit-specific patterns
# Units 0-5: respond to filter 0 (squares)
for i in range(6):
    fc1_weights[i, 0:169] = 0.02  # Connect to filter 0 outputs

# Units 6-11: respond to filter 1 (vertical lines)
for i in range(6, 12):
    fc1_weights[i, 169:338] = 0.02  # Connect to filter 1 outputs

# Units 12-17: respond to filter 2 (horizontal lines)
for i in range(12, 18):
    fc1_weights[i, 338:507] = 0.02  # Connect to filter 2 outputs

# Units 18-23: respond to filter 3 (corners) 
for i in range(18, 24):
    fc1_weights[i, 507:676] = 0.02  # Connect to filter 3 outputs

# Add small random weights to remaining connections
fc1_weights += np.random.randn(FC_HIDDEN, 13*13*CONV1_FILTERS) * 0.001
fc1_weights = to_fixed(fc1_weights)

# FC2 weights - strong connections from specific hidden units to output classes
fc2_weights = np.zeros((NUM_CLASSES, FC_HIDDEN))

# Digit 0: strong positive weights from units 0-5 (square detectors)
fc2_weights[0, 0:6] = 0.8

# Digit 1: strong positive weights from units 6-11 (vertical line detectors)
fc2_weights[1, 6:12] = 0.8

# Digit 2: positive weights from horizontal line detectors
fc2_weights[2, 12:18] = 0.5

# Digit 3: positive weights from horizontal line detectors (stronger)
fc2_weights[3, 12:18] = 0.8

# Digit 4: positive weights from corner and vertical detectors
fc2_weights[4, 18:24] = 0.6
fc2_weights[4, 6:12] = 0.3

# Add negative weights to suppress other classes
for i in range(5):
    for j in range(5):
        if i != j:
            fc2_weights[j, i*6:(i+1)*6] = -0.2

fc2_weights = to_fixed(fc2_weights)

# Save weights
with open('weights.bin', 'wb') as f:
