#include <stdlib.h>
#include <stdint.h>

#define INPUT_SIZE 784  // 28x28
#define CONV1_FILTERS 8
#define CONV1_SIZE 3
#define POOL1_SIZE 2
#define FC_HIDDEN 32
#define NUM_CLASSES 10

// Fixed-point representation: Q8.8 format
typedef int16_t fixed_t;
#define FIXED_SHIFT 8
#define FIXED_ONE (1 << FIXED_SHIFT)

// Global arrays to minimize stack usage
fixed_t conv1_weights[CONV1_FILTERS][CONV1_SIZE][CONV1_SIZE];
fixed_t conv1_bias[CONV1_FILTERS];
fixed_t fc1_weights[FC_HIDDEN][13*13*CONV1_FILTERS];
fixed_t fc2_weights[NUM_CLASSES][FC_HIDDEN];
uint8_t input_image[INPUT_SIZE];

// Very basic implementation - needs heavy optimization
void load_weights() {
    FILE *f = fopen("weights.bin", "rb");
    if (!f) return;

    // Load pre-quantized weights
    fread(conv1_weights, sizeof(conv1_weights), 1, f);
    fread(conv1_bias, sizeof(conv1_bias), 1, f);
    fread(fc1_weights, sizeof(fc1_weights), 1, f);
    fread(fc2_weights, sizeof(fc2_weights), 1, f);

    fclose(f);
}

int16_t fixed_mul(fixed_t a, fixed_t b) {
    return ((int32_t)a * b) >> FIXED_SHIFT;
}

void conv2d(fixed_t *input, fixed_t *output, int in_h, int in_w, 
            fixed_t kernel[][3], fixed_t bias) {
    for (int y = 0; y < in_h - 2; y++) {
        for (int x = 0; x < in_w - 2; x++) {
            fixed_t sum = bias;
            for (int ky = 0; ky < 3; ky++) {
                for (int kx = 0; kx < 3; kx++) {
                    sum += fixed_mul(input[(y+ky)*in_w + x+kx], kernel[ky][kx]);
                }
            }
            // ReLU activation
            output[y*(in_w-2) + x] = sum > 0 ? sum : 0;
        }
    }
}

void maxpool2d(fixed_t *input, fixed_t *output, int in_h, int in_w) {
    for (int y = 0; y < in_h/2; y++) {
        for (int x = 0; x < in_w/2; x++) {
            fixed_t max_val = input[2*y*in_w + 2*x];
            fixed_t val = input[2*y*in_w + 2*x+1];
            if (val > max_val) max_val = val;
            val = input[(2*y+1)*in_w + 2*x];
            if (val > max_val) max_val = val;
            val = input[(2*y+1)*in_w + 2*x+1];
            if (val > max_val) max_val = val;
            output[y*(in_w/2) + x] = max_val;
        }
    }
}

int forward(uint8_t *image) {
    fixed_t layer1[26*26*CONV1_FILTERS];
    fixed_t layer2[13*13*CONV1_FILTERS];
    fixed_t fc1_out[FC_HIDDEN];
    fixed_t fc2_out[NUM_CLASSES];

    // Convert input to fixed point
    fixed_t input_fixed[INPUT_SIZE];
    for (int i = 0; i < INPUT_SIZE; i++) {
        input_fixed[i] = (image[i] * FIXED_ONE) / 255;
    }

    // Conv1 + ReLU
    for (int f = 0; f < CONV1_FILTERS; f++) {
        conv2d(input_fixed, &layer1[f*26*26], 28, 28, 
               conv1_weights[f], conv1_bias[f]);
    }

    // MaxPool1
    for (int f = 0; f < CONV1_FILTERS; f++) {
        maxpool2d(&layer1[f*26*26], &layer2[f*13*13], 26, 26);
    }

    // FC1
    for (int i = 0; i < FC_HIDDEN; i++) {
        fixed_t sum = 0;
        for (int j = 0; j < 13*13*CONV1_FILTERS; j++) {
            sum += fixed_mul(layer2[j], fc1_weights[i][j]);
        }
        fc1_out[i] = sum > 0 ? sum : 0;  // ReLU
    }

    // FC2 (output layer)
    int max_idx = 0;
    fixed_t max_val = -32768;
    for (int i = 0; i < NUM_CLASSES; i++) {
        fixed_t sum = 0;
        for (int j = 0; j < FC_HIDDEN; j++) {
            sum += fixed_mul(fc1_out[j], fc2_weights[i][j]);
        }
        fc2_out[i] = sum;
        if (sum > max_val) {
            max_val = sum;
            max_idx = i;
        }
    }

    return max_idx;
}

int main() {
    load_weights();

    FILE *f = fopen("test_images.bin", "rb");
    if (!f) return 1;

    // Process 5 test images
    for (int i = 0; i < 5; i++) {
        fread(input_image, 1, INPUT_SIZE, f);
        int prediction = forward(input_image);
        printf("Image %d: Predicted class %d\n", i, prediction);
    }

    fclose(f);
    return 0;
}"}

