        if len(found_roots) != len(unique_true_roots):
            print(f"ERROR: Expected {len(unique_true_roots)} unique roots, found {len(found_roots)}")
            all_passed = False
        else:
            # Verify each found root
            all_valid = True
            for root in found_roots:
                if not solver.verify_root(f, root):
                    print(f"ERROR: Found root {root} does not satisfy f(x) ≈ 0")
                    all_valid = False
                    all_passed = False

            if all_valid:
                # Check if found roots match expected roots
                matched = True
                for true_root in unique_true_roots:
                    found_match = False
                    for found_root in found_roots:
                        if abs(found_root - true_root) < solver.tolerance * 100:
