        for j in range(9):
            if j != col:
                self.possibilities[row][j].discard(val)

        # Remove from column
        for i in range(9):
            if i != row:
                self.possibilities[i][col].discard(val)

        # Remove from box - BUG: Incorrect box calculation
        box_row, box_col = self.get_box(row, col)
