    def is_valid(self):
        # Check rows
        for i in range(9):
            nums = [self.grid[i][j] for j in range(9) if self.grid[i][j] != 0]
            if len(nums) != len(set(nums)):
                return False

        # Check columns
        for j in range(9):
            nums = [self.grid[i][j] for i in range(9) if self.grid[i][j] != 0]
            if len(nums) != len(set(nums)):
                return False

        # Check boxes
        for box_row in range(0, 9, 3):
            for box_col in range(0, 9, 3):
                nums = []
                for i in range(box_row, box_row + 3):
                    for j in range(box_col, box_col + 3):
                        if self.grid[i][j] != 0:
                            nums.append(self.grid[i][j])
                if len(nums) != len(set(nums)):
