             (timestamp TEXT, device_id TEXT, sensor_type TEXT, value REAL)''')

# Device to sensor type mapping
device_sensors = {
    'dev001': 'temperature',
    'dev002': 'humidity', 
    'dev003': 'pressure',
    'dev004': 'motion',
    'dev005': 'temperature',
    'dev006': 'humidity',
    'dev007': 'temperature',
    'dev008': 'pressure',
    'dev009': 'motion',
    'dev010': 'temperature'
}

# Generate sensor data for last 7 days
end_time = datetime.now()
start_time = end_time - timedelta(days=7)

# Normal ranges for each sensor type
ranges = {
    'temperature': (18, 28),
    'humidity': (30, 70),
    'pressure': (95, 105),
    'motion': (0, 1)
}

# Generate readings
current = start_time
while current <= end_time:
    for device_id, sensor_type in device_sensors.items():
        # Skip inactive device sometimes
        if device_id == 'dev006' and random.random() > 0.3:
            continue

        # Generate value
        if sensor_type == 'motion':
            value = random.choice([0, 1])
        else:
            min_val, max_val = ranges[sensor_type]
            value = random.uniform(min_val, max_val)

            # Add some anomalies
            if random.random() < 0.02:  # 2% chance of anomaly
                if random.random() < 0.5:
                    value = value * 1.5  # High anomaly
                else:
                    value = value * 0.5  # Low anomaly

        c.execute("INSERT INTO readings VALUES (?, ?, ?, ?)",
                 (current.isoformat(), device_id, sensor_type, value))

    # Move to next hour
    current += timedelta(hours=1)

conn.commit()
conn.close()
print("Database created successfully with sensor readings")"}

