"""
Cache manager for scientific datasets.
Provides download and offline access functionality.
"""

import os
import json
import hashlib
import boto3
from botocore.exceptions import NoCredentialsError, ClientError

class DatasetCacheManager:
    def __init__(self, config_file='dataset_config.json'):
        with open(config_file, 'r') as f:
            self.config = json.load(f)
        self.cache_dir = self.config['cache_dir']
        os.makedirs(self.cache_dir, exist_ok=True)

    def download_dataset(self, dataset_name):
        """Download a dataset from S3 to cache."""
        dataset = next((d for d in self.config['datasets'] if d['name'] == dataset_name), None)
        if not dataset:
            raise ValueError(f"Dataset {dataset_name} not found in config")

        cache_path = os.path.join(self.cache_dir, dataset_name)

        # Check if already cached
        if os.path.exists(cache_path):
            if self._verify_checksum(cache_path, dataset['checksum']):
                print(f"Dataset {dataset_name} already cached with valid checksum")
                return cache_path

        # Download from S3
        try:
            s3 = boto3.client('s3')
            print(f"Downloading {dataset_name} from S3...")
            s3.download_file(dataset['s3_bucket'], dataset['s3_key'], cache_path)

            # Verify checksum
            if not self._verify_checksum(cache_path, dataset['checksum']):
                os.remove(cache_path)
                raise ValueError(f"Checksum verification failed for {dataset_name}")

            print(f"Successfully cached {dataset_name}")
            return cache_path

        except (NoCredentialsError, ClientError) as e:
            print(f"S3 download failed: {e}")
            if os.path.exists(cache_path):
                print(f"Using existing cache for {dataset_name}")
                return cache_path
            raise

    def _verify_checksum(self, filepath, expected_checksum):
        """Verify file checksum."""
        with open(filepath, 'rb') as f:
            actual_checksum = hashlib.md5(f.read()).hexdigest()
        return actual_checksum == expected_checksum

    def get_cached_dataset(self, dataset_name):
        """Get dataset from cache only."""
        cache_path = os.path.join(self.cache_dir, dataset_name)
        if not os.path.exists(cache_path):
            raise FileNotFoundError(f"Dataset {dataset_name} not found in cache")
        return cache_path

if __name__ == '__main__':
    # Example usage
    manager = DatasetCacheManager()

    # Try to download all datasets
    for dataset in manager.config['datasets']:
