typedef struct {
    int rows;
    int cols;
    int **grid;
} Grid;

Grid* create_grid(int rows, int cols) {
    Grid *g = malloc(sizeof(Grid));
    g->rows = rows;
    g->cols = cols;
    g->grid = malloc(rows * sizeof(int*));
    for (int i = 0; i < rows; i++) {
        g->grid[i] = calloc(cols, sizeof(int));
    }
    return g;
}

void free_grid(Grid *g) {
    for (int i = 0; i < g->rows; i++) {
        free(g->grid[i]);
    }
    free(g->grid);
    free(g);
}

int count_neighbors(Grid *g, int row, int col) {
    int count = 0;
    for (int i = -1; i <= 1; i++) {
        for (int j = -1; j <= 1; j++) {
            if (i == 0 && j == 0) continue;
            int new_row = row + i;
            int new_col = col + j;
            if (new_row >= 0 && new_row < g->rows && 
                new_col >= 0 && new_col < g->cols) {
                count += g->grid[new_row][new_col];
            }
        }
    }
    return count;
}

void evolve(Grid *current, Grid *next) {
    for (int i = 0; i < current->rows; i++) {
        for (int j = 0; j < current->cols; j++) {
            int neighbors = count_neighbors(current, i, j);
            if (current->grid[i][j] == ALIVE) {
                next->grid[i][j] = (neighbors == 2 || neighbors == 3) ? ALIVE : DEAD;
            } else {
                next->grid[i][j] = (neighbors == 3) ? ALIVE : DEAD;
            }
        }
    }
