        }
    }
    return count;
}

void load_pattern(Grid *g, const char *filename) {
    FILE *f = fopen(filename, "r");
    if (!f) {
        fprintf(stderr, "Could not open pattern file\n");
        exit(1);
    }

    int row, col;
    while (fscanf(f, "%d %d", &row, &col) == 2) {
        if (row >= 0 && row < g->rows && col >= 0 && col < g->cols) {
            g->grid[row][col] = ALIVE;
        }
    }
    fclose(f);
}

int main(int argc, char *argv[]) {
    if (argc < 4) {
        fprintf(stderr, "Usage: %s <rows> <cols> <iterations> [pattern]\n", argv[0]);
        fprintf(stderr, "Pattern can be: random, glider, or filename\n");
        return 1;
    }

    int rows = atoi(argv[1]);
    int cols = atoi(argv[2]);
    int iterations = atoi(argv[3]);
    const char *pattern = (argc > 4) ? argv[4] : "random";

    Grid *current = create_grid(rows, cols);
    Grid *next = create_grid(rows, cols);

    if (strcmp(pattern, "random") == 0) {
        init_random(current, 0.3);
    } else if (strcmp(pattern, "glider") == 0) {
        init_glider(current);
    } else {
        load_pattern(current, pattern);
    }

    clock_t start = clock();

    for (int iter = 0; iter < iterations; iter++) {
        evolve(current, next);
        Grid *temp = current;
        current = next;
        next = temp;
    }

    clock_t end = clock();
    double time_spent = (double)(end - start) / CLOCKS_PER_SEC;

    printf("Grid: %dx%d\n", rows, cols);
    printf("Iterations: %d\n", iterations);
    printf("Live cells: %d\n", count_alive(current));
    printf("Time: %.6f seconds\n", time_spent);

    free_grid(current);
    free_grid(next);

    return 0;
}"}

