void monte_carlo_price(const OptionParams& params, double& call_price, double& put_price) {
    std::mt19937 gen(42);
    std::uniform_real_distribution<> dis(0.0, 1.0);

    double sum_call = 0.0;
    double sum_put = 0.0;
    double dt = params.time_to_maturity;
    double drift = (params.risk_free_rate - 0.5 * params.volatility * params.volatility) * dt;
    double diffusion = params.volatility * sqrt(dt);

    for (int i = 0; i < params.num_simulations; ++i) {
        double u1 = dis(gen);
        double u2 = dis(gen);
        double z = box_muller_transform(u1, u2);

        double stock_price = params.spot_price * exp(drift + diffusion * z);
        double call_payoff = std::max(stock_price - params.strike_price, 0.0);
        double put_payoff = std::max(params.strike_price - stock_price, 0.0);

        sum_call += call_payoff;
        sum_put += put_payoff;
    }

    double discount = exp(-params.risk_free_rate * params.time_to_maturity);
    call_price = discount * sum_call / params.num_simulations;
    put_price = discount * sum_put / params.num_simulations;
}

void print_usage(const char* program_name) {
    std::cerr << "Usage: " << program_name << " <spot> <strike> <rate> <volatility> <time> <simulations>" << std::endl;
    std::cerr << "  spot: Current price of the underlying asset" << std::endl;
    std::cerr << "  strike: Strike price of the option" << std::endl;
    std::cerr << "  rate: Risk-free interest rate (as decimal)" << std::endl;
    std::cerr << "  volatility: Volatility of the underlying (as decimal)" << std::endl;
    std::cerr << "  time: Time to maturity in years" << std::endl;
    std::cerr << "  simulations: Number of Monte Carlo simulations" << std::endl;
}

int main(int argc, char* argv[]) {
    if (argc != 7) {
        print_usage(argv[0]);
        return 1;
    }

    OptionParams params;
    params.spot_price = std::atof(argv[1]);
    params.strike_price = std::atof(argv[2]);
    params.risk_free_rate = std::atof(argv[3]);
    params.volatility = std::atof(argv[4]);
    params.time_to_maturity = std::atof(argv[5]);
    params.num_simulations = std::atoi(argv[6]);

    if (params.num_simulations <= 0) {
        std::cerr << "Error: Number of simulations must be positive" << std::endl;
        return 1;
    }

    double call_price, put_price;

    auto start = std::chrono::high_resolution_clock::now();
    monte_carlo_price(params, call_price, put_price);
    auto end = std::chrono::high_resolution_clock::now();

    std::chrono::duration<double> diff = end - start;

    std::cout << std::fixed << std::setprecision(6);
    std::cout << "Call Price: " << call_price << std::endl;
    std::cout << "Put Price: " << put_price << std::endl;
    std::cout << "Execution Time: " << diff.count() << " seconds" << std::endl;

    return 0;
